INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (213, 'Domainer', 'Просмотр быстрее и безопаснее', 'Новый браузер автоматически блокирует рекламу и трекеры, что делает его быстрее и безопаснее, чем ваш текущий браузер. Этот пакет является официальным.', null, 'gnuplot', 11, 5, '2023-02-07 11:22:45.000000', 4529, 9138);
