INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (129, 'Greenlam', 'Бесплатный и быстрый шутер', 'Захватывающий шутер от первого лица в стиле арены с четкими движениями и широким набором оружия. Он сочетает в себе интуитивно понятную механику с действиями на вашем лице, чтобы повысить частоту сердечных сокращений. Приложение есть и всегда будет бесплатной игрой. В арсенале 9 основных видов оружия и 16 полных видов оружия. У каждого есть первичный огонь и вторичный огонь, подобный UT. Каждый из них полезен в определенных ситуациях, поэтому выбор оружия имеет решающее значение. Фрагмент прочь! Благодаря поддержке нашего разнообразного сообщества приложение предлагает вам огромное разнообразие игровых режимов. Есть обычные игровые режимы, которые вы ожидаете от шутера на арене, такие как Deathmatch (FFA), Capture The Flag и Clan Arena, а также более причудливые режимы, такие как Nexball и Freeze Tag. Также поддерживается многопользовательская гонка! Приложение поставляется с 25 официальными картами, готовыми к игре, и десятками карт, созданных сообществом, одним щелчком мыши. Также поддерживаются классические карты Nexuiz, в том числе сконвертированные из Quake 3! Те, кто заинтересован в создании собственных карт, могут использовать наш редактор карт, который также поставляется с игрой.', null, 'W3C-19980720', 49, 2, '2022-10-02 16:12:13.000000', 268, 5507);
