INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (609, 'Stronghold', 'Бесплатный и быстрый шутер', 'Захватывающий шутер от первого лица в стиле арены с четкими движениями и широким набором оружия. Он сочетает в себе интуитивно понятную механику с действиями на вашем лице, чтобы повысить частоту сердечных сокращений. Приложение есть и всегда будет бесплатной игрой. В арсенале 9 основных видов оружия и 16 полных видов оружия. У каждого есть первичный огонь и вторичный огонь, подобный UT. Каждый из них полезен в определенных ситуациях, поэтому выбор оружия имеет решающее значение. Фрагмент прочь! Благодаря поддержке нашего разнообразного сообщества приложение предлагает вам огромное разнообразие игровых режимов. Есть обычные игровые режимы, которые вы ожидаете от шутера на арене, такие как Deathmatch (FFA), Capture The Flag и Clan Arena, а также более причудливые режимы, такие как Nexball и Freeze Tag. Также поддерживается многопользовательская гонка! Приложение поставляется с 25 официальными картами, готовыми к игре, и десятками карт, созданных сообществом, одним щелчком мыши. Также поддерживаются классические карты Nexuiz, в том числе сконвертированные из Quake 3! Те, кто заинтересован в создании собственных карт, могут использовать наш редактор карт, который также поставляется с игрой.', null, 'Libpng', 43, 2, '2021-10-10 04:28:25.000000', 3546, 9320);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (812, 'Domainer', '2D космический шутер', '2D космический шутер с потрясающими визуальными эффектами и привлекательной физикой. Игроки могут сражаться друг с другом или с врагами, управляемыми компьютером, в захватывающих игровых режимах. Сюжет: В 3547 году цивилизации по всей галактике заселили свои планеты, живя в мире и гармонии с окружающей средой. Но за пределами созерцательных мест обитания бушует ВЕЛИКАЯ ВОЙНА. Как известный боец ​​на пути к нескончаемой славе и процветанию, вы должны защитить свою планету от надвигающейся гибели ваших завистливых соседей!', null, 'GFDL-1.1-invariants-only', 43, 2, '2022-03-12 08:23:24.000000', 4109, 8449);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (14, 'Fixflex', 'Цифровое творчество', 'В приложении есть все традиционные инструменты, которые вы любите из других пакетов для цифровой живописи и редактирования изображений, такие как инструмент «Волшебная палочка», мощные инструменты выделения и высококачественные кисти. Но данный софт добавляет к этой смеси свою изюминку. Ваши инструменты могут не только наносить цвет, но также дополнительно материалы и неровности. Это дает изображениям, которые вы создаете, 3D-атрибуты, которые визуализируются в реальном времени на вашем графическом процессоре с использованием света и теней, что приводит к потрясающим и уникальным изображениям. Возьмем, к примеру, прикрепленный скриншот «Каменного великана». Он был окрашен светоотражающими материалами, выпуклостями и освещен двумя источниками света. Вы можете свободно перемещать источники света, чтобы увидеть, как это меняет настроение, освещение и тени сцены. Материалы и источники света в приложении используют самые современные технологии, такие как PBR и GGX.', null, 'SPL-1.0', 12, 3, '2021-06-03 00:41:03.000000', 2072, 7597);
