INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (135, 'Temp', 'Простые, но умные заметки', 'Это небольшое приложение, которое вы можете использовать в качестве приложения для быстрых заметок или задач. Маленький и простой, все внимание сосредоточено на тексте, который вы печатаете. Сохраняет на лету, нет необходимости сохранять вручную. Автоматический отступ. Складывание заметок. Используйте флажки, чтобы отслеживать свои TODO. Заменяет общий синтаксис символами, такими как стрелки. Темная и светлая тема. Портативный режим поддерживается. Бесплатно', null, 'FSFUL', 4, 5, '2021-05-12 18:06:24.000000', 802, 7044);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (678, 'Stronghold', 'Самая популярные гонки для ПК.', 'Самая популярная гоночная онлайн-игра для ПК. Он питает 7 различных гоночных сред; сотни тысяч треков и миллионы игроков, с которыми можно соревноваться. Вы можете скачать автомобиль своей мечты, раскрасить его, проехать на нем по безумным трассам, состоящим из петель, рамп и бездорожья. Все дело в вас, ваших навыках вождения и трассе перед вами. . Просто чтобы убедиться, что мы понимаем друг друга: это чистое аркадное безумие.', null, 'EUPL-1.2', 44, 2, '2022-03-04 20:41:30.000000', 1541, 6590);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (81, 'Cookley', 'Порт, управляемый сообществом', 'Сегодня вы должны помнить много паролей. Вам нужен пароль для многих веб-сайтов, вашей учетной записи электронной почты, вашего веб-сервера, входа в сеть и т. Д. Список бесконечен. Кроме того, вы должны использовать разные пароли для каждой учетной записи, потому что, если вы будете использовать только один пароль везде и кто-то получит этот пароль, у вас будет проблема: вор будет иметь доступ ко всем вашим учетным записям. Этот бесплатный менеджер паролей с открытым исходным кодом поможет вам безопасно управлять своими паролями. Вы можете хранить все свои пароли в одной базе данных, которая заблокирована мастер-ключом. Таким образом, вам нужно запомнить только один главный ключ, чтобы разблокировать всю базу данных. Файлы базы данных шифруются с использованием лучших и наиболее безопасных алгоритмов шифрования, известных в настоящее время (AES-256, ChaCha20 и Twofish). Для получения дополнительной информации см. страницу характеристик.', null, 'xinetd', 14, 6, '2022-09-01 22:50:58.000000', 4269, 9759);
