INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (623, 'It', 'Портативный пасьянс', 'Пасья́нс (фр. patience — терпение) — карточная игра для одного человека. Играющий раскладывает карты, придерживаясь определённых правил и, чаще всего, преследуя некоторую цель. В зависимости от правил цель может быть достижима в той или иной степени благодаря интеллектуальным усилиям играющего и благодаря случайности (зависящей от расклада). Последнее позволяет использовать пасьянсы для гадания. ', null, 'libselinux-1.0', 42, 2, '2021-06-11 19:30:10.000000', 3195, 5937);
