INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (128, 'Andalax', 'Лучшее почтовое приложение', 'Это новая версия почтового клиента, поддерживаемая одним из первоначальных авторов. Это быстрее, компактнее и с доставкой уже сегодня! Он заменяет код синхронизации JavaScript новым механизмом синхронизации C++, основанным на Mailcore2. Он использует намного меньше ОЗУ и ЦП и работает без «пробуждения ЦП», что означает большое время автономной работы. Он также имеет полностью обновленный композитор и другие замечательные новые функции. Приложение поставляется с мощными функциями, такими как Unified Inbox, Snooze, Send Later, Mail Rules, Templates и другими. Pro режим, который вы можете разблокировать с ежемесячной подпиской, добавляет еще больше функций для людей, которые отправляют тонны электронной почты: отслеживание ссылок, уведомления о прочтении, аналитика почтовых ящиков, контакты и профили компаний. Все эти функции работают в клиенте — приложение не отправляет ваши учетные данные электронной почты в облако.', null, 'SSH-short', 29, 5, '2022-06-12 23:32:22.000000', 4222, 7724);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (999, 'Voyatouch', 'Flutter SDK', 'Flutter — бесплатный и открытый набор средств разработки мобильного пользовательского интерфейса, созданный компанией Google и выпущенный в мае 2017 года. Проще говоря, с помощью Flutter возможно создать собственное мобильное приложение с одним массивом кода. Это означает, что для создания двух приложений (IOS и Android) можно использовать единый язык программирования и одну базу кода. Flutter нацелен на две важные вещи: SDK (Software Development Kit): набор инструментов, который поможет вам в разработке приложений. Он включает инструменты для компиляции кода в нативном машинном коде (код для IOS и Android) и Framework (Библиотека пользовательского интерфейса на основе виджетов): Коллекция функциональных элементов пользовательского интерфейса (кнопок, текстовых вводов, ползунков и т.д.), которые можно персонализировать под личные предпочтения. Для разработки с Flutter используется язык программирования под названием Dart. Это также язык Google, созданный в октябре 2011 года, но значительно улучшившийся в последние годы. Dart фокусируется на развитии вёрстки веб-страниц; его можно с легкостью использовать для создания мобильных и веб-приложений.', null, 'CC-BY-NC-SA-2.0', 15, 1, '2021-02-05 02:32:28.000000', 3685, 5185);
