INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (418, 'Vagram', 'Лучшее почтовое приложение', 'Это новая версия почтового клиента, поддерживаемая одним из первоначальных авторов. Это быстрее, компактнее и с доставкой уже сегодня! Он заменяет код синхронизации JavaScript новым механизмом синхронизации C++, основанным на Mailcore2. Он использует намного меньше ОЗУ и ЦП и работает без «пробуждения ЦП», что означает большое время автономной работы. Он также имеет полностью обновленный композитор и другие замечательные новые функции. Приложение поставляется с мощными функциями, такими как Unified Inbox, Snooze, Send Later, Mail Rules, Templates и другими. Pro режим, который вы можете разблокировать с ежемесячной подпиской, добавляет еще больше функций для людей, которые отправляют тонны электронной почты: отслеживание ссылок, уведомления о прочтении, аналитика почтовых ящиков, контакты и профили компаний. Все эти функции работают в клиенте — приложение не отправляет ваши учетные данные электронной почты в облако.', null, 'APSL-1.0', 1, 5, '2021-08-16 04:44:15.000000', 4043, 7576);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (419, 'Zathin', 'Канбан с открытым исходным кодом', 'Это приложение для совместной работы с канбан-досками с открытым исходным кодом. Независимо от того, ведете ли вы личный список дел, планируете отпуск с друзьями или работаете в команде над своей следующей революционной идеей, доски Канбан — непревзойденный инструмент для организации ваших дел. Они дают вам визуальный обзор текущего состояния вашего проекта и повышают вашу продуктивность, позволяя сосредоточиться на нескольких наиболее важных элементах. В зависимости от целевой среды может потребоваться изменение некоторых параметров конфигурации. Для получения полного списка опций конфигурации ознакомьтесь на сайте', null, 'NICTA-1.0', 17, 5, '2021-07-04 10:50:49.000000', 4541, 7320);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (431, 'Sub-Ex', 'Ваш личный дневник', 'Отправляйтесь на путь самосовершенствования к лучшим качествам любви, жизни и здоровья. Записывайте свои ежедневные события, секреты, благодарности и переживайте эти моменты. Приложение, которому доверяют миллионы пользователей, — ваш идеальный компаньон для ведения дневника, который сохранит ваши личные воспоминания на всю жизнь. # Перспективный цифровой журнал, созданный для того, чтобы журнал служил вечно, дает вам полный контроль над вашим личным дневником. # Перенесите свои воспоминания куда угодно  # Развивайте привычку вести дневник # Легкое ведение дневника. Добавьте фотографии или видео и запишите свои мысли. Он делает все остальное, автоматически добавляя погоду и места в запись журнала. # Оживите моменты красиво. Просматривайте свой журнал на временной шкале, повторно посещайте места, показанные на карте, и отправляйтесь в прошлое с календарем. # Частный дневник с PIN-кодом. Держите дневник в секрете с паролем. # Импорт и экспорт журнала в различные форматы.', null, 'GPL-1.0-only', 22, 5, '2022-03-29 00:07:54.000000', 4514, 9685);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (435, 'Y-find', 'Простые, но умные заметки', 'Это небольшое приложение, которое вы можете использовать в качестве приложения для быстрых заметок или задач. Маленький и простой, все внимание сосредоточено на тексте, который вы печатаете. Сохраняет на лету, нет необходимости сохранять вручную. Автоматический отступ. Складывание заметок. Используйте флажки, чтобы отслеживать свои TODO. Заменяет общий синтаксис символами, такими как стрелки. Темная и светлая тема. Портативный режим поддерживается. Бесплатно', null, 'TCP-wrappers', 9, 5, '2021-09-17 14:39:15.000000', 3835, 9560);
