INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (198, 'Vagram', 'Виртуальный световой стол и фотолаборатория', 'Это приложение для работы с фотографиями с открытым исходным кодом и разработчик RAW. Виртуальный световой стол и фотолаборатория для фотографов. Он управляет вашими цифровыми негативами в базе данных, позволяет просматривать их с помощью масштабируемого светового стола и позволяет вам обрабатывать необработанные изображения и улучшать их.', null, 'MTLL', 34, 3, '2023-02-12 00:38:39.000000', 4139, 8389);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (66, 'Zathin', 'Командируй силами', 'В игру вы командуете силами Проекта в битве за восстановление мира после того, как человечество было почти уничтожено ядерными ракетами. Игра предлагает режимы кампании, многопользовательской и одиночной перестрелки. Обширное дерево технологий с более чем 400 различными технологиями в сочетании с системой проектирования юнитов позволяет создавать самые разные юниты и тактики. Изначально игра была разработана Cole Studios как коммерческая игра и опубликована в 1999 году, а затем выпущена ими с открытым исходным кодом в 2004 году. С тех пор она разрабатывается, поддерживается и улучшается сообществом под знаменем Проект 2100.', null, 'Zed', 9, 2, '2023-02-06 16:13:11.000000', 4585, 7340);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (5, 'Biodex', 'Для управления репозиториями', 'Миллионы разработчиков и команд по всему миру выбирают данный клиент, чтобы заставить Git работать на них. Это делает Git более наглядным и доступным благодаря интуитивно понятному пользовательскому интерфейсу, гибкому переключению между графическим интерфейсом и терминалом, а также удобной работе в разных операционных системах. Интеграция с GitHub, GitLab, Bitbucket и Azure DevOps позволяет быстро и просто клонировать, разветвлять и добавлять удаленные устройства. Вы даже можете создавать, просматривать и управлять запросами на вытягивание. Возможности клиента: Просматривайте историю ветвлений, слияний и коммитов на темном или светлом графике, Упростите сложные команды, такие как слияние, перебазирование и вставка, в действия перетаскивания, Переключитесь с вкладки «Репозиторий» на вкладку «Терминал», чтобы испытать революционный «клавиатурный» терминал, улучшенный Git, который поможет вам быстрее управлять Git, Групповые репозитории и быстрое подключение новых членов команды с помощью Workspaces, Интеграция с GitHub, GitHub Enterprise, GitLab, GitLab Self-Hosted, Bitbucket, Bitbucket Server и Azure DevOps, Интегрируйтесь с популярными системами отслеживания проблем, такими как Jira, GitHub Issues, GitLab Issues и Trello, Создавайте, клонируйте и добавляйте удаленные репозитории из интегрированных сервисов, Создание и просмотр запросов на вытягивание из интегрированных служб.', null, 'LPPL-1.3a', 27, 1, '2022-06-11 14:23:20.000000', 3196, 8624);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (6, 'Cookley', 'Расширяемая платформа', 'Приложение является бесплатной программной платформой с открытым исходным кодом. Написана на языке программирования Java и основной целью её создания является повышение продуктивности процесса разработки программного обеспечения. Претендует на статус наиболее популярной Java IDE и является единственным конкурентом такой мощной платформы как NetBeans. Но в отличие от NetBeans который для создания элементов пользовательского интерфейса использует платформо-независимую библиотеку Swing, в данной IDE используется платформо-зависимая библиотека SWT - Standard Widget Toolkit. IDE разработанные на базе данной платформы применяются для создания программного обеспечения на различных языках программирования, так как данное приложение является платформой для разработки любых интегрированных сред программирования и расширений для себя же ', null, 'OML', 37, 1, '2023-01-27 09:27:33.000000', 3587, 6997);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (743, 'Transcof', 'Портативный пасьянс', 'Пасья́нс (фр. patience — терпение) — карточная игра для одного человека. Играющий раскладывает карты, придерживаясь определённых правил и, чаще всего, преследуя некоторую цель. В зависимости от правил цель может быть достижима в той или иной степени благодаря интеллектуальным усилиям играющего и благодаря случайности (зависящей от расклада). Последнее позволяет использовать пасьянсы для гадания. ', null, 'JasPer-2.0', 16, 2, '2022-10-23 23:16:00.000000', 4193, 9503);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (30, 'Prodder', 'Работа с данными на новом уровне', 'Это мощный инструмент для работы с базами данных. Он поддерживает самые разные базы данных, включая PostgreSQL, MySQL, SQL Server, Oracle, MongoDB, а также источники данных DDL. С помощью проводника баз данных можно легко просматривать структуру базы данных и выполнять другие действия. Основные возможности: копирование и вставка данных, бастрый поиск (Просто начните набирать нужное слово — поверх окна проводника баз данных появится текстовое поле с введенными символами. При этом в списке будет выделен первый элемент, удовлетворяющий запросу, и подсвечены совпадающие символы в строках. Для выбора нужного элемента можно использовать клавиши со стрелками. Обратите внимание, что быстрый поиск выполняется только для развернутых узлов), группировка по папкам, а также кправление узлами вторичных объектов', null, 'curl', 19, 1, '2022-06-04 15:09:55.000000', 412, 9678);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (38, 'It', 'Переобпределение кода', 'Это редактор кода для разных языков программирования. Он относительно немного весит, гибкий и удобный. В нем можно писать, форматировать и редактировать код на разных языках. Приложение меньше весит чем его аналоги, проще в освоении и подходит в том числе для начинающих разработчиков. Редактор кода существует для всех популярных операционных систем. Он бесплатный, и с ним работают программисты на разных языках. Например, им активно пользуются веб-разработчики, пишущие на HTML/CSS, JavaScript, PHP. Но редактор поддерживает намного большее количество языков: Python, Go, Ruby, C#, TypeScript и так далее. Он работает и с расширениями и фреймворками для популярных языков — например, с React JS и Vue.js, с языками стилей SCSS и LESS, которые дополняют CSS. В теории пользоваться редактором может разработчик практически на любом из современных языков. Но на практике его применяют там, где не нужны мощности полноценной IDE.', null, 'LGPL-3.0-only', 34, 1, '2021-11-08 18:24:52.000000', 3401, 7874);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (44, 'Otcom', 'Мультифункциональный блокнот', 'Это текстовый редактор, который можно использовать и для работы с текстами, и для написания и редактирования кода. Он похож на обычный «Блокнот», но функциональнее. Иногда приложение используют в качестве редактора кода. Так он стал известен среди разработчиков. Сейчас, несмотря на обилие IDE и специализированных редакторов, приложение все еще используют для специфических языков программирования и сред. Функциональность приложения — нечто среднее между редактором текста и кода. Его можно использовать для множества задач, он довольно универсален. Вот несколько примеров, для чего может пригодиться редактор. Для заметок. Софт поддерживает языки разметки, на которых пишут технические заметки и документацию, например Markdown или LaTeX. Он знает их синтаксис и выделяет все по правилам. Поэтому техническому специалисту, привыкшему описывать все с помощью специальных языков, будет удобно писать заметки с помощью этого инструмента. Для текстового контента. Также приложение является текстовым редактором, а это значит, что в нем можно писать разнообразные тексты. В отличие от обычного «Блокнота», он поддерживает вкладки: пользователь может одновременно открыть несколько текстов в разных вкладках. Выше и возможности для форматирования. В приложении по умолчанию включена поддержка Markdown — разметки, с помощью которой можно изменять текст. Можно создавать заголовки и списки, делать участки текста полужирными или наклонными — все базовые возможности есть. Для записи кода. Причина, по которой редактором пользуются айтишники, — в нем можно писать код. Он поддерживает и узнаёт множество языков программирования, от популярных до очень редких. К тому же данный рендактор гибко настраивается и в нем есть множество возможностей, удобных разработчикам. Он фактически повторяет часть функциональности профессиональных IDE или редакторов кода, но при этом куда меньше весит и требует мало оперативной памяти.', null, 'Hippocratic-2.1', 24, 1, '2021-12-22 11:58:43.000000', 4191, 7872);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (45, 'Vagram', 'Для управления репозиториями', 'Миллионы разработчиков и команд по всему миру выбирают данный клиент, чтобы заставить Git работать на них. Это делает Git более наглядным и доступным благодаря интуитивно понятному пользовательскому интерфейсу, гибкому переключению между графическим интерфейсом и терминалом, а также удобной работе в разных операционных системах. Интеграция с GitHub, GitLab, Bitbucket и Azure DevOps позволяет быстро и просто клонировать, разветвлять и добавлять удаленные устройства. Вы даже можете создавать, просматривать и управлять запросами на вытягивание. Возможности клиента: Просматривайте историю ветвлений, слияний и коммитов на темном или светлом графике, Упростите сложные команды, такие как слияние, перебазирование и вставка, в действия перетаскивания, Переключитесь с вкладки «Репозиторий» на вкладку «Терминал», чтобы испытать революционный «клавиатурный» терминал, улучшенный Git, который поможет вам быстрее управлять Git, Групповые репозитории и быстрое подключение новых членов команды с помощью Workspaces, Интеграция с GitHub, GitHub Enterprise, GitLab, GitLab Self-Hosted, Bitbucket, Bitbucket Server и Azure DevOps, Интегрируйтесь с популярными системами отслеживания проблем, такими как Jira, GitHub Issues, GitLab Issues и Trello, Создавайте, клонируйте и добавляйте удаленные репозитории из интегрированных сервисов, Создание и просмотр запросов на вытягивание из интегрированных служб.', null, 'CPAL-1.0', 40, 1, '2022-12-05 17:55:20.000000', 466, 7115);
