INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (96, 'Tresom', 'Расширяемая платформа', 'Приложение является бесплатной программной платформой с открытым исходным кодом. Написана на языке программирования Java и основной целью её создания является повышение продуктивности процесса разработки программного обеспечения. Претендует на статус наиболее популярной Java IDE и является единственным конкурентом такой мощной платформы как NetBeans. Но в отличие от NetBeans который для создания элементов пользовательского интерфейса использует платформо-независимую библиотеку Swing, в данной IDE используется платформо-зависимая библиотека SWT - Standard Widget Toolkit. IDE разработанные на базе данной платформы применяются для создания программного обеспечения на различных языках программирования, так как данное приложение является платформой для разработки любых интегрированных сред программирования и расширений для себя же ', null, 'PostgreSQL', 21, 1, '2022-06-09 21:23:31.000000', 3217, 6474);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (532, 'Span', 'Веб-браузер', 'Проект браузера с открытым исходным кодом, целью которого является создание более безопасного, быстрого и стабильного способа работы в Интернете для всех пользователей Интернета.', null, 'dvipdfm', 46, 5, '2022-04-07 18:00:26.000000', 2733, 5270);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (124, 'Toughjoyfax', 'Мультифункциональный блокнот', 'Это текстовый редактор, который можно использовать и для работы с текстами, и для написания и редактирования кода. Он похож на обычный «Блокнот», но функциональнее. Иногда приложение используют в качестве редактора кода. Так он стал известен среди разработчиков. Сейчас, несмотря на обилие IDE и специализированных редакторов, приложение все еще используют для специфических языков программирования и сред. Функциональность приложения — нечто среднее между редактором текста и кода. Его можно использовать для множества задач, он довольно универсален. Вот несколько примеров, для чего может пригодиться редактор. Для заметок. Софт поддерживает языки разметки, на которых пишут технические заметки и документацию, например Markdown или LaTeX. Он знает их синтаксис и выделяет все по правилам. Поэтому техническому специалисту, привыкшему описывать все с помощью специальных языков, будет удобно писать заметки с помощью этого инструмента. Для текстового контента. Также приложение является текстовым редактором, а это значит, что в нем можно писать разнообразные тексты. В отличие от обычного «Блокнота», он поддерживает вкладки: пользователь может одновременно открыть несколько текстов в разных вкладках. Выше и возможности для форматирования. В приложении по умолчанию включена поддержка Markdown — разметки, с помощью которой можно изменять текст. Можно создавать заголовки и списки, делать участки текста полужирными или наклонными — все базовые возможности есть. Для записи кода. Причина, по которой редактором пользуются айтишники, — в нем можно писать код. Он поддерживает и узнаёт множество языков программирования, от популярных до очень редких. К тому же данный рендактор гибко настраивается и в нем есть множество возможностей, удобных разработчикам. Он фактически повторяет часть функциональности профессиональных IDE или редакторов кода, но при этом куда меньше весит и требует мало оперативной памяти.', null, 'BSD-3-Clause-No-Nuclear-License-2014', 34, 1, '2022-06-03 14:17:16.000000', 1470, 6174);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (125, 'Sonair', 'Для управления репозиториями', 'Миллионы разработчиков и команд по всему миру выбирают данный клиент, чтобы заставить Git работать на них. Это делает Git более наглядным и доступным благодаря интуитивно понятному пользовательскому интерфейсу, гибкому переключению между графическим интерфейсом и терминалом, а также удобной работе в разных операционных системах. Интеграция с GitHub, GitLab, Bitbucket и Azure DevOps позволяет быстро и просто клонировать, разветвлять и добавлять удаленные устройства. Вы даже можете создавать, просматривать и управлять запросами на вытягивание. Возможности клиента: Просматривайте историю ветвлений, слияний и коммитов на темном или светлом графике, Упростите сложные команды, такие как слияние, перебазирование и вставка, в действия перетаскивания, Переключитесь с вкладки «Репозиторий» на вкладку «Терминал», чтобы испытать революционный «клавиатурный» терминал, улучшенный Git, который поможет вам быстрее управлять Git, Групповые репозитории и быстрое подключение новых членов команды с помощью Workspaces, Интеграция с GitHub, GitHub Enterprise, GitLab, GitLab Self-Hosted, Bitbucket, Bitbucket Server и Azure DevOps, Интегрируйтесь с популярными системами отслеживания проблем, такими как Jira, GitHub Issues, GitLab Issues и Trello, Создавайте, клонируйте и добавляйте удаленные репозитории из интегрированных сервисов, Создание и просмотр запросов на вытягивание из интегрированных служб.', null, 'CC-BY-NC-SA-2.0', 1, 1, '2021-01-21 23:41:06.000000', 1374, 7761);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (148, 'Viva', 'Переобпределение кода', 'Это редактор кода для разных языков программирования. Он относительно немного весит, гибкий и удобный. В нем можно писать, форматировать и редактировать код на разных языках. Приложение меньше весит чем его аналоги, проще в освоении и подходит в том числе для начинающих разработчиков. Редактор кода существует для всех популярных операционных систем. Он бесплатный, и с ним работают программисты на разных языках. Например, им активно пользуются веб-разработчики, пишущие на HTML/CSS, JavaScript, PHP. Но редактор поддерживает намного большее количество языков: Python, Go, Ruby, C#, TypeScript и так далее. Он работает и с расширениями и фреймворками для популярных языков — например, с React JS и Vue.js, с языками стилей SCSS и LESS, которые дополняют CSS. В теории пользоваться редактором может разработчик практически на любом из современных языков. Но на практике его применяют там, где не нужны мощности полноценной IDE.', null, 'UCL-1.0', 32, 1, '2022-03-08 19:01:09.000000', 4205, 7346);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (150, 'Home Ing', 'Работа с данными на новом уровне', 'Это мощный инструмент для работы с базами данных. Он поддерживает самые разные базы данных, включая PostgreSQL, MySQL, SQL Server, Oracle, MongoDB, а также источники данных DDL. С помощью проводника баз данных можно легко просматривать структуру базы данных и выполнять другие действия. Основные возможности: копирование и вставка данных, бастрый поиск (Просто начните набирать нужное слово — поверх окна проводника баз данных появится текстовое поле с введенными символами. При этом в списке будет выделен первый элемент, удовлетворяющий запросу, и подсвечены совпадающие символы в строках. Для выбора нужного элемента можно использовать клавиши со стрелками. Обратите внимание, что быстрый поиск выполняется только для развернутых узлов), группировка по папкам, а также кправление узлами вторичных объектов', null, 'Borceux', 41, 1, '2021-02-12 13:55:52.000000', 773, 6828);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (167, 'Alpha', 'Мощная и эргономичная Java IDE', 'IDE, интегрированная среда разработки (комплекс программных средств, который используется для написания, исполнения, отладки и оптимизации кода) для Java, JavaScript, Python и других языков программирования от компании SoftCloud. Отличается обширным набором инструментов для рефакторинга (перепроектирования) и оптимизации кода. Данная версия является платной. И предпназначена для фулстек-разработки и создания корпоративных приложений. Поддерживает широкий набор фреймворков и технологий для бэкенда и фронтенда и включает инструменты для профилирования и работы с базами данных, HTTP-клиент и много других функций. Все возможности пакета можно протестировать бесплатно в течение 30 дней, а при оформлении заявки на командное тестирование — в течение 90 дней.', null, 'AFL-3.0', 40, 1, '2021-07-15 21:43:35.000000', 615, 8521);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (171, 'Treeflex', 'Сложный текстовый редактор для кода', 'Это мощный многоплатформенный текстовый редактор с чистым современным пользовательским интерфейсом. Предлагая широкую поддержку языков программирования, расширений, функций на основе синтаксиса и многих других полезных функций наряду с низким потреблением ресурсов, он зарекомендовал себя как очень компетентное решение для широкого набора сред и приложений. Программное обеспечение можно загрузить и оценить бесплатно, однако для дальнейшего использования необходимо приобрести лицензию.', null, 'copyleft-next-0.3.1', 23, 1, '2021-01-08 16:10:21.000000', 4926, 7744);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (173, 'Bitchip', 'Среда разработки Android', 'Интегрированная среда разработки производства Capstore, с помощью которой разработчикам становятся доступны инструменты для создания приложений на платформе Android OS. IDE можно загрузить и пользоваться бесплатно. В ней присутствуют макеты для создания UI, с чего обычно начинается работа над приложением. В приложении содержатся инструменты для разработки решений для смартфонов и планшетов, а также новые технологические решения для Android TV, Android Wear, Android Auto, Glass и дополнительные контекстуальные модули. Среда предназначена как для небольших команд разработчиков мобильных приложений (даже в количестве одного человека), или же крупных международных организаций с GIT или другими подобными системами управления версиями. Опытные разработчики смогут выбрать инструменты, которые больше подходят для масштабных проектов. Решения для Android разрабатываются с использованием Java или C++. В основе рабочего процесса заложен концепт непрерывной интеграции, позволяющий сразу же обнаруживать имеющиеся проблемы. Продолжительная проверка кода обеспечивает возможность эффективной обратной связи с разработчиками. Такая опция позволяет быстрее опубликовать версию мобильного приложения в Google Play App Store. Для этого присутствует также поддержка инструментов LINT, Pro-Guard и App Signing.', null, 'ANTLR-PD', 6, 1, '2021-05-30 22:11:20.000000', 3465, 7873);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (420, 'Transcof', 'Бесплатный профессиональный редактор изображений', 'Бесплатный профессиональный редактор изображений с полным ускорением графического процессора. Поддерживает следующий функционал: слои, маски, стили слоя, фильтры и слои интерактивных фильтров, поддержка HDR, поддержка графического планшета, полное ускорение графического процессора.', null, 'Linux-man-pages-copyleft', 22, 3, '2021-07-05 10:23:00.000000', 2915, 6880);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (177, 'Andalax', 'Мощная и эргономичная Java IDE', 'IDE, интегрированная среда разработки (комплекс программных средств, который используется для написания, исполнения, отладки и оптимизации кода) для Java, JavaScript, Python и других языков программирования от компании SoftCloud. Отличается обширным набором инструментов для рефакторинга (перепроектирования) и оптимизации кода. Данная версия является платной. И предпназначена для фулстек-разработки и создания корпоративных приложений. Поддерживает широкий набор фреймворков и технологий для бэкенда и фронтенда и включает инструменты для профилирования и работы с базами данных, HTTP-клиент и много других функций. Все возможности пакета можно протестировать бесплатно в течение 30 дней, а при оформлении заявки на командное тестирование — в течение 90 дней.', null, 'EPL-1.0', 31, 1, '2023-01-11 06:13:21.000000', 1901, 5024);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (189, 'Opela', 'Flutter SDK', 'Flutter — бесплатный и открытый набор средств разработки мобильного пользовательского интерфейса, созданный компанией Google и выпущенный в мае 2017 года. Проще говоря, с помощью Flutter возможно создать собственное мобильное приложение с одним массивом кода. Это означает, что для создания двух приложений (IOS и Android) можно использовать единый язык программирования и одну базу кода. Flutter нацелен на две важные вещи: SDK (Software Development Kit): набор инструментов, который поможет вам в разработке приложений. Он включает инструменты для компиляции кода в нативном машинном коде (код для IOS и Android) и Framework (Библиотека пользовательского интерфейса на основе виджетов): Коллекция функциональных элементов пользовательского интерфейса (кнопок, текстовых вводов, ползунков и т.д.), которые можно персонализировать под личные предпочтения. Для разработки с Flutter используется язык программирования под названием Dart. Это также язык Google, созданный в октябре 2011 года, но значительно улучшившийся в последние годы. Dart фокусируется на развитии вёрстки веб-страниц; его можно с легкостью использовать для создания мобильных и веб-приложений.', null, 'BSD-2-Clause-Patent', 40, 1, '2023-01-24 20:29:52.000000', 109, 7949);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (831, 'Quo Lux', 'Сложный текстовый редактор для кода', 'Это мощный многоплатформенный текстовый редактор с чистым современным пользовательским интерфейсом. Предлагая широкую поддержку языков программирования, расширений, функций на основе синтаксиса и многих других полезных функций наряду с низким потреблением ресурсов, он зарекомендовал себя как очень компетентное решение для широкого набора сред и приложений. Программное обеспечение можно загрузить и оценить бесплатно, однако для дальнейшего использования необходимо приобрести лицензию.', null, 'Hippocratic-2.1', 15, 1, '2023-01-23 20:11:19.000000', 1229, 7049);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (475, 'Lotlux', 'Простые, но умные заметки', 'Это небольшое приложение, которое вы можете использовать в качестве приложения для быстрых заметок или задач. Маленький и простой, все внимание сосредоточено на тексте, который вы печатаете. Сохраняет на лету, нет необходимости сохранять вручную. Автоматический отступ. Складывание заметок. Используйте флажки, чтобы отслеживать свои TODO. Заменяет общий синтаксис символами, такими как стрелки. Темная и светлая тема. Портативный режим поддерживается. Бесплатно', null, 'FreeBSD-DOC', 37, 5, '2021-01-23 03:46:43.000000', 4900, 6557);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (178, 'Bamity', 'Переобпределение кода', 'Это редактор кода для разных языков программирования. Он относительно немного весит, гибкий и удобный. В нем можно писать, форматировать и редактировать код на разных языках. Приложение меньше весит чем его аналоги, проще в освоении и подходит в том числе для начинающих разработчиков. Редактор кода существует для всех популярных операционных систем. Он бесплатный, и с ним работают программисты на разных языках. Например, им активно пользуются веб-разработчики, пишущие на HTML/CSS, JavaScript, PHP. Но редактор поддерживает намного большее количество языков: Python, Go, Ruby, C#, TypeScript и так далее. Он работает и с расширениями и фреймворками для популярных языков — например, с React JS и Vue.js, с языками стилей SCSS и LESS, которые дополняют CSS. В теории пользоваться редактором может разработчик практически на любом из современных языков. Но на практике его применяют там, где не нужны мощности полноценной IDE.', null, 'Unicode-DFS-2015', 26, 1, '2023-02-07 14:17:36.000000', 3615, 5863);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (183, 'Bytecard', 'Среда разработки Android', 'Интегрированная среда разработки производства Capstore, с помощью которой разработчикам становятся доступны инструменты для создания приложений на платформе Android OS. IDE можно загрузить и пользоваться бесплатно. В ней присутствуют макеты для создания UI, с чего обычно начинается работа над приложением. В приложении содержатся инструменты для разработки решений для смартфонов и планшетов, а также новые технологические решения для Android TV, Android Wear, Android Auto, Glass и дополнительные контекстуальные модули. Среда предназначена как для небольших команд разработчиков мобильных приложений (даже в количестве одного человека), или же крупных международных организаций с GIT или другими подобными системами управления версиями. Опытные разработчики смогут выбрать инструменты, которые больше подходят для масштабных проектов. Решения для Android разрабатываются с использованием Java или C++. В основе рабочего процесса заложен концепт непрерывной интеграции, позволяющий сразу же обнаруживать имеющиеся проблемы. Продолжительная проверка кода обеспечивает возможность эффективной обратной связи с разработчиками. Такая опция позволяет быстрее опубликовать версию мобильного приложения в Google Play App Store. Для этого присутствует также поддержка инструментов LINT, Pro-Guard и App Signing.', null, 'ANTLR-PD-fallback', 50, 1, '2022-01-04 05:39:57.000000', 4425, 8210);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (981, 'Y-Solowarm', 'Сложный текстовый редактор для кода', 'Это мощный многоплатформенный текстовый редактор с чистым современным пользовательским интерфейсом. Предлагая широкую поддержку языков программирования, расширений, функций на основе синтаксиса и многих других полезных функций наряду с низким потреблением ресурсов, он зарекомендовал себя как очень компетентное решение для широкого набора сред и приложений. Программное обеспечение можно загрузить и оценить бесплатно, однако для дальнейшего использования необходимо приобрести лицензию.', null, 'ErlPL-1.1', 6, 1, '2022-03-23 02:38:27.000000', 3299, 8311);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (185, 'Aerified', 'Для управления репозиториями', 'Миллионы разработчиков и команд по всему миру выбирают данный клиент, чтобы заставить Git работать на них. Это делает Git более наглядным и доступным благодаря интуитивно понятному пользовательскому интерфейсу, гибкому переключению между графическим интерфейсом и терминалом, а также удобной работе в разных операционных системах. Интеграция с GitHub, GitLab, Bitbucket и Azure DevOps позволяет быстро и просто клонировать, разветвлять и добавлять удаленные устройства. Вы даже можете создавать, просматривать и управлять запросами на вытягивание. Возможности клиента: Просматривайте историю ветвлений, слияний и коммитов на темном или светлом графике, Упростите сложные команды, такие как слияние, перебазирование и вставка, в действия перетаскивания, Переключитесь с вкладки «Репозиторий» на вкладку «Терминал», чтобы испытать революционный «клавиатурный» терминал, улучшенный Git, который поможет вам быстрее управлять Git, Групповые репозитории и быстрое подключение новых членов команды с помощью Workspaces, Интеграция с GitHub, GitHub Enterprise, GitLab, GitLab Self-Hosted, Bitbucket, Bitbucket Server и Azure DevOps, Интегрируйтесь с популярными системами отслеживания проблем, такими как Jira, GitHub Issues, GitLab Issues и Trello, Создавайте, клонируйте и добавляйте удаленные репозитории из интегрированных сервисов, Создание и просмотр запросов на вытягивание из интегрированных служб.', null, 'TMate', 18, 1, '2021-01-27 19:12:10.000000', 1701, 9871);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (186, 'Fix San', 'Расширяемая платформа', 'Приложение является бесплатной программной платформой с открытым исходным кодом. Написана на языке программирования Java и основной целью её создания является повышение продуктивности процесса разработки программного обеспечения. Претендует на статус наиболее популярной Java IDE и является единственным конкурентом такой мощной платформы как NetBeans. Но в отличие от NetBeans который для создания элементов пользовательского интерфейса использует платформо-независимую библиотеку Swing, в данной IDE используется платформо-зависимая библиотека SWT - Standard Widget Toolkit. IDE разработанные на базе данной платформы применяются для создания программного обеспечения на различных языках программирования, так как данное приложение является платформой для разработки любых интегрированных сред программирования и расширений для себя же ', null, 'CC-BY-NC-ND-3.0-DE', 15, 1, '2021-03-13 01:05:22.000000', 4652, 7048);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (476, 'Sonsing', 'Мощный браузер', 'Это мощный расширяемый веб-браузер с поддержкой современных технологий веб-приложений.', null, 'TCL', 50, 5, '2021-12-14 19:30:22.000000', 3176, 6716);
