INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (204, 'Tin', 'Мультифункциональный блокнот', 'Это текстовый редактор, который можно использовать и для работы с текстами, и для написания и редактирования кода. Он похож на обычный «Блокнот», но функциональнее. Иногда приложение используют в качестве редактора кода. Так он стал известен среди разработчиков. Сейчас, несмотря на обилие IDE и специализированных редакторов, приложение все еще используют для специфических языков программирования и сред. Функциональность приложения — нечто среднее между редактором текста и кода. Его можно использовать для множества задач, он довольно универсален. Вот несколько примеров, для чего может пригодиться редактор. Для заметок. Софт поддерживает языки разметки, на которых пишут технические заметки и документацию, например Markdown или LaTeX. Он знает их синтаксис и выделяет все по правилам. Поэтому техническому специалисту, привыкшему описывать все с помощью специальных языков, будет удобно писать заметки с помощью этого инструмента. Для текстового контента. Также приложение является текстовым редактором, а это значит, что в нем можно писать разнообразные тексты. В отличие от обычного «Блокнота», он поддерживает вкладки: пользователь может одновременно открыть несколько текстов в разных вкладках. Выше и возможности для форматирования. В приложении по умолчанию включена поддержка Markdown — разметки, с помощью которой можно изменять текст. Можно создавать заголовки и списки, делать участки текста полужирными или наклонными — все базовые возможности есть. Для записи кода. Причина, по которой редактором пользуются айтишники, — в нем можно писать код. Он поддерживает и узнаёт множество языков программирования, от популярных до очень редких. К тому же данный рендактор гибко настраивается и в нем есть множество возможностей, удобных разработчикам. Он фактически повторяет часть функциональности профессиональных IDE или редакторов кода, но при этом куда меньше весит и требует мало оперативной памяти.', null, 'XSkat', 9, 1, '2021-07-19 06:02:38.000000', 1392, 9603);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (212, 'Bigtax', 'Среда разработки API', 'Это полный набор инструментов для разработчиков API, которым пользуются более 3 миллионов разработчиков и 30 000 компаний по всему миру. Приложение ускоряет и упрощает работу с API, поддерживая разработчиков на каждом этапе их рабочего процесса, и доступен для пользователей различных платформ. Это сервис, который используется для ручного и автоматизированного тестирования HTTP API. С его помощью можно выполнять любые запросы через удобный веб-интерфейс, создавать тесты работы API в автоматическом режиме и многое другое. ', null, 'SMPPL', 38, 1, '2022-05-28 05:37:08.000000', 1037, 8905);
