INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (731, 'Tempsoft', 'Сложный текстовый редактор для кода', 'Это мощный многоплатформенный текстовый редактор с чистым современным пользовательским интерфейсом. Предлагая широкую поддержку языков программирования, расширений, функций на основе синтаксиса и многих других полезных функций наряду с низким потреблением ресурсов, он зарекомендовал себя как очень компетентное решение для широкого набора сред и приложений. Программное обеспечение можно загрузить и оценить бесплатно, однако для дальнейшего использования необходимо приобрести лицензию.', null, 'MIT-open-group', 20, 1, '2022-10-27 04:29:00.000000', 3660, 7847);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (225, 'Bytecard', 'Для управления репозиториями', 'Миллионы разработчиков и команд по всему миру выбирают данный клиент, чтобы заставить Git работать на них. Это делает Git более наглядным и доступным благодаря интуитивно понятному пользовательскому интерфейсу, гибкому переключению между графическим интерфейсом и терминалом, а также удобной работе в разных операционных системах. Интеграция с GitHub, GitLab, Bitbucket и Azure DevOps позволяет быстро и просто клонировать, разветвлять и добавлять удаленные устройства. Вы даже можете создавать, просматривать и управлять запросами на вытягивание. Возможности клиента: Просматривайте историю ветвлений, слияний и коммитов на темном или светлом графике, Упростите сложные команды, такие как слияние, перебазирование и вставка, в действия перетаскивания, Переключитесь с вкладки «Репозиторий» на вкладку «Терминал», чтобы испытать революционный «клавиатурный» терминал, улучшенный Git, который поможет вам быстрее управлять Git, Групповые репозитории и быстрое подключение новых членов команды с помощью Workspaces, Интеграция с GitHub, GitHub Enterprise, GitLab, GitLab Self-Hosted, Bitbucket, Bitbucket Server и Azure DevOps, Интегрируйтесь с популярными системами отслеживания проблем, такими как Jira, GitHub Issues, GitLab Issues и Trello, Создавайте, клонируйте и добавляйте удаленные репозитории из интегрированных сервисов, Создание и просмотр запросов на вытягивание из интегрированных служб.', null, 'mpich2', 1, 1, '2022-04-22 18:08:45.000000', 1178, 5554);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (232, 'Solarbreeze', 'Среда разработки API', 'Это полный набор инструментов для разработчиков API, которым пользуются более 3 миллионов разработчиков и 30 000 компаний по всему миру. Приложение ускоряет и упрощает работу с API, поддерживая разработчиков на каждом этапе их рабочего процесса, и доступен для пользователей различных платформ. Это сервис, который используется для ручного и автоматизированного тестирования HTTP API. С его помощью можно выполнять любые запросы через удобный веб-интерфейс, создавать тесты работы API в автоматическом режиме и многое другое. ', null, 'CERN-OHL-1.1', 8, 1, '2021-03-05 16:31:04.000000', 983, 5848);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (234, 'Regrant', 'Мультифункциональный блокнот', 'Это текстовый редактор, который можно использовать и для работы с текстами, и для написания и редактирования кода. Он похож на обычный «Блокнот», но функциональнее. Иногда приложение используют в качестве редактора кода. Так он стал известен среди разработчиков. Сейчас, несмотря на обилие IDE и специализированных редакторов, приложение все еще используют для специфических языков программирования и сред. Функциональность приложения — нечто среднее между редактором текста и кода. Его можно использовать для множества задач, он довольно универсален. Вот несколько примеров, для чего может пригодиться редактор. Для заметок. Софт поддерживает языки разметки, на которых пишут технические заметки и документацию, например Markdown или LaTeX. Он знает их синтаксис и выделяет все по правилам. Поэтому техническому специалисту, привыкшему описывать все с помощью специальных языков, будет удобно писать заметки с помощью этого инструмента. Для текстового контента. Также приложение является текстовым редактором, а это значит, что в нем можно писать разнообразные тексты. В отличие от обычного «Блокнота», он поддерживает вкладки: пользователь может одновременно открыть несколько текстов в разных вкладках. Выше и возможности для форматирования. В приложении по умолчанию включена поддержка Markdown — разметки, с помощью которой можно изменять текст. Можно создавать заголовки и списки, делать участки текста полужирными или наклонными — все базовые возможности есть. Для записи кода. Причина, по которой редактором пользуются айтишники, — в нем можно писать код. Он поддерживает и узнаёт множество языков программирования, от популярных до очень редких. К тому же данный рендактор гибко настраивается и в нем есть множество возможностей, удобных разработчикам. Он фактически повторяет часть функциональности профессиональных IDE или редакторов кода, но при этом куда меньше весит и требует мало оперативной памяти.', null, 'GFDL-1.3-only', 46, 1, '2022-12-02 23:52:38.000000', 3823, 5511);
