INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (807, 'Job', 'Бесплатный офисный пакет', 'Это бесплатный офисный пакет с открытым исходным кодом, разработанный The Mark Corp. Пакет включает программы для обработки текстов, создания и редактирования электронных таблиц, слайд-шоу, диаграмм и рисунков, работы с базами данных и составления математических формул.', null, 'RPSL-1.0', 4, 5, '2021-01-01 22:41:53.000000', 1648, 6797);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (276, 'Daltfresh', 'Расширяемая платформа', 'Приложение является бесплатной программной платформой с открытым исходным кодом. Написана на языке программирования Java и основной целью её создания является повышение продуктивности процесса разработки программного обеспечения. Претендует на статус наиболее популярной Java IDE и является единственным конкурентом такой мощной платформы как NetBeans. Но в отличие от NetBeans который для создания элементов пользовательского интерфейса использует платформо-независимую библиотеку Swing, в данной IDE используется платформо-зависимая библиотека SWT - Standard Widget Toolkit. IDE разработанные на базе данной платформы применяются для создания программного обеспечения на различных языках программирования, так как данное приложение является платформой для разработки любых интегрированных сред программирования и расширений для себя же ', null, 'CPAL-1.0', 46, 1, '2022-09-17 13:42:08.000000', 2450, 6111);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (290, 'Cardguard', 'Работа с данными на новом уровне', 'Это мощный инструмент для работы с базами данных. Он поддерживает самые разные базы данных, включая PostgreSQL, MySQL, SQL Server, Oracle, MongoDB, а также источники данных DDL. С помощью проводника баз данных можно легко просматривать структуру базы данных и выполнять другие действия. Основные возможности: копирование и вставка данных, бастрый поиск (Просто начните набирать нужное слово — поверх окна проводника баз данных появится текстовое поле с введенными символами. При этом в списке будет выделен первый элемент, удовлетворяющий запросу, и подсвечены совпадающие символы в строках. Для выбора нужного элемента можно использовать клавиши со стрелками. Обратите внимание, что быстрый поиск выполняется только для развернутых узлов), группировка по папкам, а также кправление узлами вторичных объектов', null, 'SugarCRM-1.1.3', 30, 1, '2021-06-03 10:37:30.000000', 4819, 6096);
