INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (392, 'Bytecard', 'Среда разработки API', 'Это полный набор инструментов для разработчиков API, которым пользуются более 3 миллионов разработчиков и 30 000 компаний по всему миру. Приложение ускоряет и упрощает работу с API, поддерживая разработчиков на каждом этапе их рабочего процесса, и доступен для пользователей различных платформ. Это сервис, который используется для ручного и автоматизированного тестирования HTTP API. С его помощью можно выполнять любые запросы через удобный веб-интерфейс, создавать тесты работы API в автоматическом режиме и многое другое. ', null, 'Eurosym', 32, 1, '2021-10-25 18:03:07.000000', 3433, 6818);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (303, 'Tempsoft', 'Среда разработки Android', 'Интегрированная среда разработки производства Capstore, с помощью которой разработчикам становятся доступны инструменты для создания приложений на платформе Android OS. IDE можно загрузить и пользоваться бесплатно. В ней присутствуют макеты для создания UI, с чего обычно начинается работа над приложением. В приложении содержатся инструменты для разработки решений для смартфонов и планшетов, а также новые технологические решения для Android TV, Android Wear, Android Auto, Glass и дополнительные контекстуальные модули. Среда предназначена как для небольших команд разработчиков мобильных приложений (даже в количестве одного человека), или же крупных международных организаций с GIT или другими подобными системами управления версиями. Опытные разработчики смогут выбрать инструменты, которые больше подходят для масштабных проектов. Решения для Android разрабатываются с использованием Java или C++. В основе рабочего процесса заложен концепт непрерывной интеграции, позволяющий сразу же обнаруживать имеющиеся проблемы. Продолжительная проверка кода обеспечивает возможность эффективной обратной связи с разработчиками. Такая опция позволяет быстрее опубликовать версию мобильного приложения в Google Play App Store. Для этого присутствует также поддержка инструментов LINT, Pro-Guard и App Signing.', null, 'NCSA', 15, 1, '2021-03-30 22:13:16.000000', 3822, 9412);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (304, 'Andalax', 'Мультифункциональный блокнот', 'Это текстовый редактор, который можно использовать и для работы с текстами, и для написания и редактирования кода. Он похож на обычный «Блокнот», но функциональнее. Иногда приложение используют в качестве редактора кода. Так он стал известен среди разработчиков. Сейчас, несмотря на обилие IDE и специализированных редакторов, приложение все еще используют для специфических языков программирования и сред. Функциональность приложения — нечто среднее между редактором текста и кода. Его можно использовать для множества задач, он довольно универсален. Вот несколько примеров, для чего может пригодиться редактор. Для заметок. Софт поддерживает языки разметки, на которых пишут технические заметки и документацию, например Markdown или LaTeX. Он знает их синтаксис и выделяет все по правилам. Поэтому техническому специалисту, привыкшему описывать все с помощью специальных языков, будет удобно писать заметки с помощью этого инструмента. Для текстового контента. Также приложение является текстовым редактором, а это значит, что в нем можно писать разнообразные тексты. В отличие от обычного «Блокнота», он поддерживает вкладки: пользователь может одновременно открыть несколько текстов в разных вкладках. Выше и возможности для форматирования. В приложении по умолчанию включена поддержка Markdown — разметки, с помощью которой можно изменять текст. Можно создавать заголовки и списки, делать участки текста полужирными или наклонными — все базовые возможности есть. Для записи кода. Причина, по которой редактором пользуются айтишники, — в нем можно писать код. Он поддерживает и узнаёт множество языков программирования, от популярных до очень редких. К тому же данный рендактор гибко настраивается и в нем есть множество возможностей, удобных разработчикам. Он фактически повторяет часть функциональности профессиональных IDE или редакторов кода, но при этом куда меньше весит и требует мало оперативной памяти.', null, 'PHP-3.0', 8, 1, '2022-08-05 08:51:14.000000', 2982, 9427);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (305, 'Bigtax', 'Для управления репозиториями', 'Миллионы разработчиков и команд по всему миру выбирают данный клиент, чтобы заставить Git работать на них. Это делает Git более наглядным и доступным благодаря интуитивно понятному пользовательскому интерфейсу, гибкому переключению между графическим интерфейсом и терминалом, а также удобной работе в разных операционных системах. Интеграция с GitHub, GitLab, Bitbucket и Azure DevOps позволяет быстро и просто клонировать, разветвлять и добавлять удаленные устройства. Вы даже можете создавать, просматривать и управлять запросами на вытягивание. Возможности клиента: Просматривайте историю ветвлений, слияний и коммитов на темном или светлом графике, Упростите сложные команды, такие как слияние, перебазирование и вставка, в действия перетаскивания, Переключитесь с вкладки «Репозиторий» на вкладку «Терминал», чтобы испытать революционный «клавиатурный» терминал, улучшенный Git, который поможет вам быстрее управлять Git, Групповые репозитории и быстрое подключение новых членов команды с помощью Workspaces, Интеграция с GitHub, GitHub Enterprise, GitLab, GitLab Self-Hosted, Bitbucket, Bitbucket Server и Azure DevOps, Интегрируйтесь с популярными системами отслеживания проблем, такими как Jira, GitHub Issues, GitLab Issues и Trello, Создавайте, клонируйте и добавляйте удаленные репозитории из интегрированных сервисов, Создание и просмотр запросов на вытягивание из интегрированных служб.', null, 'BSD-2-Clause-Views', 40, 1, '2022-02-14 11:05:50.000000', 3722, 9299);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (310, 'Domainer', 'Работа с данными на новом уровне', 'Это мощный инструмент для работы с базами данных. Он поддерживает самые разные базы данных, включая PostgreSQL, MySQL, SQL Server, Oracle, MongoDB, а также источники данных DDL. С помощью проводника баз данных можно легко просматривать структуру базы данных и выполнять другие действия. Основные возможности: копирование и вставка данных, бастрый поиск (Просто начните набирать нужное слово — поверх окна проводника баз данных появится текстовое поле с введенными символами. При этом в списке будет выделен первый элемент, удовлетворяющий запросу, и подсвечены совпадающие символы в строках. Для выбора нужного элемента можно использовать клавиши со стрелками. Обратите внимание, что быстрый поиск выполняется только для развернутых узлов), группировка по папкам, а также кправление узлами вторичных объектов', null, 'OGDL-Taiwan-1.0', 37, 1, '2022-05-31 21:21:28.000000', 4791, 5495);
