INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (368, 'Treeflex', 'Переобпределение кода', 'Это редактор кода для разных языков программирования. Он относительно немного весит, гибкий и удобный. В нем можно писать, форматировать и редактировать код на разных языках. Приложение меньше весит чем его аналоги, проще в освоении и подходит в том числе для начинающих разработчиков. Редактор кода существует для всех популярных операционных систем. Он бесплатный, и с ним работают программисты на разных языках. Например, им активно пользуются веб-разработчики, пишущие на HTML/CSS, JavaScript, PHP. Но редактор поддерживает намного большее количество языков: Python, Go, Ruby, C#, TypeScript и так далее. Он работает и с расширениями и фреймворками для популярных языков — например, с React JS и Vue.js, с языками стилей SCSS и LESS, которые дополняют CSS. В теории пользоваться редактором может разработчик практически на любом из современных языков. Но на практике его применяют там, где не нужны мощности полноценной IDE.', null, 'ErlPL-1.1', 40, 1, '2022-10-07 02:43:59.000000', 1780, 7209);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (898, 'Bigtax', 'Самая популярные гонки для ПК.', 'Самая популярная гоночная онлайн-игра для ПК. Он питает 7 различных гоночных сред; сотни тысяч треков и миллионы игроков, с которыми можно соревноваться. Вы можете скачать автомобиль своей мечты, раскрасить его, проехать на нем по безумным трассам, состоящим из петель, рамп и бездорожья. Все дело в вас, ваших навыках вождения и трассе перед вами. . Просто чтобы убедиться, что мы понимаем друг друга: это чистое аркадное безумие.', null, 'VSL-1.0', 38, 2, '2021-07-27 20:24:05.000000', 2956, 6093);
