INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (519, 'Otcom', 'Flutter SDK', 'Flutter — бесплатный и открытый набор средств разработки мобильного пользовательского интерфейса, созданный компанией Google и выпущенный в мае 2017 года. Проще говоря, с помощью Flutter возможно создать собственное мобильное приложение с одним массивом кода. Это означает, что для создания двух приложений (IOS и Android) можно использовать единый язык программирования и одну базу кода. Flutter нацелен на две важные вещи: SDK (Software Development Kit): набор инструментов, который поможет вам в разработке приложений. Он включает инструменты для компиляции кода в нативном машинном коде (код для IOS и Android) и Framework (Библиотека пользовательского интерфейса на основе виджетов): Коллекция функциональных элементов пользовательского интерфейса (кнопок, текстовых вводов, ползунков и т.д.), которые можно персонализировать под личные предпочтения. Для разработки с Flutter используется язык программирования под названием Dart. Это также язык Google, созданный в октябре 2011 года, но значительно улучшившийся в последние годы. Dart фокусируется на развитии вёрстки веб-страниц; его можно с легкостью использовать для создания мобильных и веб-приложений.', null, 'CERN-OHL-1.1', 40, 1, '2021-11-14 02:02:38.000000', 2918, 7115);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (535, 'Sonsing', 'Для управления репозиториями', 'Миллионы разработчиков и команд по всему миру выбирают данный клиент, чтобы заставить Git работать на них. Это делает Git более наглядным и доступным благодаря интуитивно понятному пользовательскому интерфейсу, гибкому переключению между графическим интерфейсом и терминалом, а также удобной работе в разных операционных системах. Интеграция с GitHub, GitLab, Bitbucket и Azure DevOps позволяет быстро и просто клонировать, разветвлять и добавлять удаленные устройства. Вы даже можете создавать, просматривать и управлять запросами на вытягивание. Возможности клиента: Просматривайте историю ветвлений, слияний и коммитов на темном или светлом графике, Упростите сложные команды, такие как слияние, перебазирование и вставка, в действия перетаскивания, Переключитесь с вкладки «Репозиторий» на вкладку «Терминал», чтобы испытать революционный «клавиатурный» терминал, улучшенный Git, который поможет вам быстрее управлять Git, Групповые репозитории и быстрое подключение новых членов команды с помощью Workspaces, Интеграция с GitHub, GitHub Enterprise, GitLab, GitLab Self-Hosted, Bitbucket, Bitbucket Server и Azure DevOps, Интегрируйтесь с популярными системами отслеживания проблем, такими как Jira, GitHub Issues, GitLab Issues и Trello, Создавайте, клонируйте и добавляйте удаленные репозитории из интегрированных сервисов, Создание и просмотр запросов на вытягивание из интегрированных служб.', null, 'FTL', 14, 1, '2021-06-27 00:51:32.000000', 3605, 7757);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (580, 'Viva', 'Работа с данными на новом уровне', 'Это мощный инструмент для работы с базами данных. Он поддерживает самые разные базы данных, включая PostgreSQL, MySQL, SQL Server, Oracle, MongoDB, а также источники данных DDL. С помощью проводника баз данных можно легко просматривать структуру базы данных и выполнять другие действия. Основные возможности: копирование и вставка данных, бастрый поиск (Просто начните набирать нужное слово — поверх окна проводника баз данных появится текстовое поле с введенными символами. При этом в списке будет выделен первый элемент, удовлетворяющий запросу, и подсвечены совпадающие символы в строках. Для выбора нужного элемента можно использовать клавиши со стрелками. Обратите внимание, что быстрый поиск выполняется только для развернутых узлов), группировка по папкам, а также кправление узлами вторичных объектов', null, 'Condor-1.1', 9, 1, '2021-09-08 10:34:06.000000', 4406, 6843);
