INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (621, 'Opela', 'Сложный текстовый редактор для кода', 'Это мощный многоплатформенный текстовый редактор с чистым современным пользовательским интерфейсом. Предлагая широкую поддержку языков программирования, расширений, функций на основе синтаксиса и многих других полезных функций наряду с низким потреблением ресурсов, он зарекомендовал себя как очень компетентное решение для широкого набора сред и приложений. Программное обеспечение можно загрузить и оценить бесплатно, однако для дальнейшего использования необходимо приобрести лицензию.', null, 'LiLiQ-Rplus-1.1', 32, 1, '2022-03-24 16:17:13.000000', 2767, 9081);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (625, 'Ventosanzap', 'Для управления репозиториями', 'Миллионы разработчиков и команд по всему миру выбирают данный клиент, чтобы заставить Git работать на них. Это делает Git более наглядным и доступным благодаря интуитивно понятному пользовательскому интерфейсу, гибкому переключению между графическим интерфейсом и терминалом, а также удобной работе в разных операционных системах. Интеграция с GitHub, GitLab, Bitbucket и Azure DevOps позволяет быстро и просто клонировать, разветвлять и добавлять удаленные устройства. Вы даже можете создавать, просматривать и управлять запросами на вытягивание. Возможности клиента: Просматривайте историю ветвлений, слияний и коммитов на темном или светлом графике, Упростите сложные команды, такие как слияние, перебазирование и вставка, в действия перетаскивания, Переключитесь с вкладки «Репозиторий» на вкладку «Терминал», чтобы испытать революционный «клавиатурный» терминал, улучшенный Git, который поможет вам быстрее управлять Git, Групповые репозитории и быстрое подключение новых членов команды с помощью Workspaces, Интеграция с GitHub, GitHub Enterprise, GitLab, GitLab Self-Hosted, Bitbucket, Bitbucket Server и Azure DevOps, Интегрируйтесь с популярными системами отслеживания проблем, такими как Jira, GitHub Issues, GitLab Issues и Trello, Создавайте, клонируйте и добавляйте удаленные репозитории из интегрированных сервисов, Создание и просмотр запросов на вытягивание из интегрированных служб.', null, 'FDK-AAC', 49, 1, '2022-04-03 17:38:49.000000', 2468, 8139);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (633, 'Subin', 'Среда разработки Android', 'Интегрированная среда разработки производства Capstore, с помощью которой разработчикам становятся доступны инструменты для создания приложений на платформе Android OS. IDE можно загрузить и пользоваться бесплатно. В ней присутствуют макеты для создания UI, с чего обычно начинается работа над приложением. В приложении содержатся инструменты для разработки решений для смартфонов и планшетов, а также новые технологические решения для Android TV, Android Wear, Android Auto, Glass и дополнительные контекстуальные модули. Среда предназначена как для небольших команд разработчиков мобильных приложений (даже в количестве одного человека), или же крупных международных организаций с GIT или другими подобными системами управления версиями. Опытные разработчики смогут выбрать инструменты, которые больше подходят для масштабных проектов. Решения для Android разрабатываются с использованием Java или C++. В основе рабочего процесса заложен концепт непрерывной интеграции, позволяющий сразу же обнаруживать имеющиеся проблемы. Продолжительная проверка кода обеспечивает возможность эффективной обратной связи с разработчиками. Такая опция позволяет быстрее опубликовать версию мобильного приложения в Google Play App Store. Для этого присутствует также поддержка инструментов LINT, Pro-Guard и App Signing.', null, 'Zend-2.0', 23, 1, '2022-03-19 09:20:48.000000', 4948, 8512);
