INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (503, 'Sonsing', 'Инфраструктурный узел', 'Tor состоит из клиента, как минимум трех промежуточных узлов и общедоступной конечной точки, и в результате пользователи, стремящиеся к конфиденциальности, передают свой веб-, ftp-, ssh-трафик и другой трафик по сети, получателям, наблюдателям и даже ретрансляторам. сами с трудом отслеживают источник потока. Этот пакет предоставляет только промежуточные узлы, необходимые для надежной сети, но не предоставляет точку входа и не создает точку выхода.', null, 'Eurosym', 31, 6, '2021-11-22 23:14:26.000000', 1495, 5462);
