INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (252, 'Namfix', '2D космический шутер', '2D космический шутер с потрясающими визуальными эффектами и привлекательной физикой. Игроки могут сражаться друг с другом или с врагами, управляемыми компьютером, в захватывающих игровых режимах. Сюжет: В 3547 году цивилизации по всей галактике заселили свои планеты, живя в мире и гармонии с окружающей средой. Но за пределами созерцательных мест обитания бушует ВЕЛИКАЯ ВОЙНА. Как известный боец ​​на пути к нескончаемой славе и процветанию, вы должны защитить свою планету от надвигающейся гибели ваших завистливых соседей!', null, 'BSD-4-Clause-UC', 1, 2, '2023-02-28 16:49:30.000000', 4774, 6289);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (866, 'Bitchip', 'Расширяемая платформа', 'Приложение является бесплатной программной платформой с открытым исходным кодом. Написана на языке программирования Java и основной целью её создания является повышение продуктивности процесса разработки программного обеспечения. Претендует на статус наиболее популярной Java IDE и является единственным конкурентом такой мощной платформы как NetBeans. Но в отличие от NetBeans который для создания элементов пользовательского интерфейса использует платформо-независимую библиотеку Swing, в данной IDE используется платформо-зависимая библиотека SWT - Standard Widget Toolkit. IDE разработанные на базе данной платформы применяются для создания программного обеспечения на различных языках программирования, так как данное приложение является платформой для разработки любых интегрированных сред программирования и расширений для себя же ', null, 'HTMLTIDY', 11, 1, '2022-10-13 12:46:31.000000', 2470, 8835);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (879, 'Tin', 'Flutter SDK', 'Flutter — бесплатный и открытый набор средств разработки мобильного пользовательского интерфейса, созданный компанией Google и выпущенный в мае 2017 года. Проще говоря, с помощью Flutter возможно создать собственное мобильное приложение с одним массивом кода. Это означает, что для создания двух приложений (IOS и Android) можно использовать единый язык программирования и одну базу кода. Flutter нацелен на две важные вещи: SDK (Software Development Kit): набор инструментов, который поможет вам в разработке приложений. Он включает инструменты для компиляции кода в нативном машинном коде (код для IOS и Android) и Framework (Библиотека пользовательского интерфейса на основе виджетов): Коллекция функциональных элементов пользовательского интерфейса (кнопок, текстовых вводов, ползунков и т.д.), которые можно персонализировать под личные предпочтения. Для разработки с Flutter используется язык программирования под названием Dart. Это также язык Google, созданный в октябре 2011 года, но значительно улучшившийся в последние годы. Dart фокусируется на развитии вёрстки веб-страниц; его можно с легкостью использовать для создания мобильных и веб-приложений.', null, 'Noweb', 36, 1, '2022-01-14 16:18:19.000000', 4693, 9287);
