INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (358, 'Gembucket', 'Самая популярные гонки для ПК.', 'Самая популярная гоночная онлайн-игра для ПК. Он питает 7 различных гоночных сред; сотни тысяч треков и миллионы игроков, с которыми можно соревноваться. Вы можете скачать автомобиль своей мечты, раскрасить его, проехать на нем по безумным трассам, состоящим из петель, рамп и бездорожья. Все дело в вас, ваших навыках вождения и трассе перед вами. . Просто чтобы убедиться, что мы понимаем друг друга: это чистое аркадное безумие.', null, 'FreeImage', 34, 2, '2021-05-09 18:46:51.000000', 4385, 8602);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (900, 'Pannier', 'Работа с данными на новом уровне', 'Это мощный инструмент для работы с базами данных. Он поддерживает самые разные базы данных, включая PostgreSQL, MySQL, SQL Server, Oracle, MongoDB, а также источники данных DDL. С помощью проводника баз данных можно легко просматривать структуру базы данных и выполнять другие действия. Основные возможности: копирование и вставка данных, бастрый поиск (Просто начните набирать нужное слово — поверх окна проводника баз данных появится текстовое поле с введенными символами. При этом в списке будет выделен первый элемент, удовлетворяющий запросу, и подсвечены совпадающие символы в строках. Для выбора нужного элемента можно использовать клавиши со стрелками. Обратите внимание, что быстрый поиск выполняется только для развернутых узлов), группировка по папкам, а также кправление узлами вторичных объектов', null, 'Dotseqn', 8, 1, '2022-04-14 00:53:00.000000', 3977, 8613);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (907, 'Job', 'Мощная и эргономичная Java IDE', 'IDE, интегрированная среда разработки (комплекс программных средств, который используется для написания, исполнения, отладки и оптимизации кода) для Java, JavaScript, Python и других языков программирования от компании SoftCloud. Отличается обширным набором инструментов для рефакторинга (перепроектирования) и оптимизации кода. Данная версия является платной. И предпназначена для фулстек-разработки и создания корпоративных приложений. Поддерживает широкий набор фреймворков и технологий для бэкенда и фронтенда и включает инструменты для профилирования и работы с базами данных, HTTP-клиент и много других функций. Все возможности пакета можно протестировать бесплатно в течение 30 дней, а при оформлении заявки на командное тестирование — в течение 90 дней.', null, 'GFDL-1.1-no-invariants-or-later', 29, 1, '2022-10-20 01:06:18.000000', 1918, 9969);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (909, 'Bytecard', 'Flutter SDK', 'Flutter — бесплатный и открытый набор средств разработки мобильного пользовательского интерфейса, созданный компанией Google и выпущенный в мае 2017 года. Проще говоря, с помощью Flutter возможно создать собственное мобильное приложение с одним массивом кода. Это означает, что для создания двух приложений (IOS и Android) можно использовать единый язык программирования и одну базу кода. Flutter нацелен на две важные вещи: SDK (Software Development Kit): набор инструментов, который поможет вам в разработке приложений. Он включает инструменты для компиляции кода в нативном машинном коде (код для IOS и Android) и Framework (Библиотека пользовательского интерфейса на основе виджетов): Коллекция функциональных элементов пользовательского интерфейса (кнопок, текстовых вводов, ползунков и т.д.), которые можно персонализировать под личные предпочтения. Для разработки с Flutter используется язык программирования под названием Dart. Это также язык Google, созданный в октябре 2011 года, но значительно улучшившийся в последние годы. Dart фокусируется на развитии вёрстки веб-страниц; его можно с легкостью использовать для создания мобильных и веб-приложений.', null, 'OLDAP-1.3', 44, 1, '2022-04-28 15:54:24.000000', 3524, 5888);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (835, 'Greenlam', 'Анонимный VPN', 'Это простой, быстрый и безопасный VPN-сервис. Приложение не требует учетной записи пользователя, не ведет журналы и не отслеживает вас каким-либо образом. Услуга полностью оплачивается за счет пожертвований таких пользователей, как вы.', null, 'ECL-2.0', 35, 6, '2022-05-30 17:34:32.000000', 1623, 8608);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (938, 'Opela', 'Переобпределение кода', 'Это редактор кода для разных языков программирования. Он относительно немного весит, гибкий и удобный. В нем можно писать, форматировать и редактировать код на разных языках. Приложение меньше весит чем его аналоги, проще в освоении и подходит в том числе для начинающих разработчиков. Редактор кода существует для всех популярных операционных систем. Он бесплатный, и с ним работают программисты на разных языках. Например, им активно пользуются веб-разработчики, пишущие на HTML/CSS, JavaScript, PHP. Но редактор поддерживает намного большее количество языков: Python, Go, Ruby, C#, TypeScript и так далее. Он работает и с расширениями и фреймворками для популярных языков — например, с React JS и Vue.js, с языками стилей SCSS и LESS, которые дополняют CSS. В теории пользоваться редактором может разработчик практически на любом из современных языков. Но на практике его применяют там, где не нужны мощности полноценной IDE.', null, 'xpp', 9, 1, '2022-02-10 02:22:58.000000', 506, 9104);
