INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (973, 'Gembucket', 'Среда разработки Android', 'Интегрированная среда разработки производства Capstore, с помощью которой разработчикам становятся доступны инструменты для создания приложений на платформе Android OS. IDE можно загрузить и пользоваться бесплатно. В ней присутствуют макеты для создания UI, с чего обычно начинается работа над приложением. В приложении содержатся инструменты для разработки решений для смартфонов и планшетов, а также новые технологические решения для Android TV, Android Wear, Android Auto, Glass и дополнительные контекстуальные модули. Среда предназначена как для небольших команд разработчиков мобильных приложений (даже в количестве одного человека), или же крупных международных организаций с GIT или другими подобными системами управления версиями. Опытные разработчики смогут выбрать инструменты, которые больше подходят для масштабных проектов. Решения для Android разрабатываются с использованием Java или C++. В основе рабочего процесса заложен концепт непрерывной интеграции, позволяющий сразу же обнаруживать имеющиеся проблемы. Продолжительная проверка кода обеспечивает возможность эффективной обратной связи с разработчиками. Такая опция позволяет быстрее опубликовать версию мобильного приложения в Google Play App Store. Для этого присутствует также поддержка инструментов LINT, Pro-Guard и App Signing.', null, 'CAL-1.0', 1, 1, '2022-04-28 18:10:29.000000', 3802, 7464);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (983, 'Y-Solowarm', 'Среда разработки Android', 'Интегрированная среда разработки производства Capstore, с помощью которой разработчикам становятся доступны инструменты для создания приложений на платформе Android OS. IDE можно загрузить и пользоваться бесплатно. В ней присутствуют макеты для создания UI, с чего обычно начинается работа над приложением. В приложении содержатся инструменты для разработки решений для смартфонов и планшетов, а также новые технологические решения для Android TV, Android Wear, Android Auto, Glass и дополнительные контекстуальные модули. Среда предназначена как для небольших команд разработчиков мобильных приложений (даже в количестве одного человека), или же крупных международных организаций с GIT или другими подобными системами управления версиями. Опытные разработчики смогут выбрать инструменты, которые больше подходят для масштабных проектов. Решения для Android разрабатываются с использованием Java или C++. В основе рабочего процесса заложен концепт непрерывной интеграции, позволяющий сразу же обнаруживать имеющиеся проблемы. Продолжительная проверка кода обеспечивает возможность эффективной обратной связи с разработчиками. Такая опция позволяет быстрее опубликовать версию мобильного приложения в Google Play App Store. Для этого присутствует также поддержка инструментов LINT, Pro-Guard и App Signing.', null, 'ZPL-2.0', 47, 1, '2021-09-19 11:56:19.000000', 1800, 5527);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (855, 'Sonsing', 'Простые, но умные заметки', 'Это небольшое приложение, которое вы можете использовать в качестве приложения для быстрых заметок или задач. Маленький и простой, все внимание сосредоточено на тексте, который вы печатаете. Сохраняет на лету, нет необходимости сохранять вручную. Автоматический отступ. Складывание заметок. Используйте флажки, чтобы отслеживать свои TODO. Заменяет общий синтаксис символами, такими как стрелки. Темная и светлая тема. Портативный режим поддерживается. Бесплатно', null, 'UPL-1.0', 24, 5, '2021-05-06 00:01:21.000000', 4559, 9991);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (984, 'Voyatouch', 'Мультифункциональный блокнот', 'Это текстовый редактор, который можно использовать и для работы с текстами, и для написания и редактирования кода. Он похож на обычный «Блокнот», но функциональнее. Иногда приложение используют в качестве редактора кода. Так он стал известен среди разработчиков. Сейчас, несмотря на обилие IDE и специализированных редакторов, приложение все еще используют для специфических языков программирования и сред. Функциональность приложения — нечто среднее между редактором текста и кода. Его можно использовать для множества задач, он довольно универсален. Вот несколько примеров, для чего может пригодиться редактор. Для заметок. Софт поддерживает языки разметки, на которых пишут технические заметки и документацию, например Markdown или LaTeX. Он знает их синтаксис и выделяет все по правилам. Поэтому техническому специалисту, привыкшему описывать все с помощью специальных языков, будет удобно писать заметки с помощью этого инструмента. Для текстового контента. Также приложение является текстовым редактором, а это значит, что в нем можно писать разнообразные тексты. В отличие от обычного «Блокнота», он поддерживает вкладки: пользователь может одновременно открыть несколько текстов в разных вкладках. Выше и возможности для форматирования. В приложении по умолчанию включена поддержка Markdown — разметки, с помощью которой можно изменять текст. Можно создавать заголовки и списки, делать участки текста полужирными или наклонными — все базовые возможности есть. Для записи кода. Причина, по которой редактором пользуются айтишники, — в нем можно писать код. Он поддерживает и узнаёт множество языков программирования, от популярных до очень редких. К тому же данный рендактор гибко настраивается и в нем есть множество возможностей, удобных разработчикам. Он фактически повторяет часть функциональности профессиональных IDE или редакторов кода, но при этом куда меньше весит и требует мало оперативной памяти.', null, 'Linux-man-pages-copyleft', 27, 1, '2022-01-15 09:24:19.000000', 4082, 6624);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (382, 'Konklux', '2D космический шутер', '2D космический шутер с потрясающими визуальными эффектами и привлекательной физикой. Игроки могут сражаться друг с другом или с врагами, управляемыми компьютером, в захватывающих игровых режимах. Сюжет: В 3547 году цивилизации по всей галактике заселили свои планеты, живя в мире и гармонии с окружающей средой. Но за пределами созерцательных мест обитания бушует ВЕЛИКАЯ ВОЙНА. Как известный боец ​​на пути к нескончаемой славе и процветанию, вы должны защитить свою планету от надвигающейся гибели ваших завистливых соседей!', null, 'OGL-Canada-2.0', 41, 2, '2022-09-04 14:29:41.000000', 461, 5975);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (989, 'Tresom', 'Flutter SDK', 'Flutter — бесплатный и открытый набор средств разработки мобильного пользовательского интерфейса, созданный компанией Google и выпущенный в мае 2017 года. Проще говоря, с помощью Flutter возможно создать собственное мобильное приложение с одним массивом кода. Это означает, что для создания двух приложений (IOS и Android) можно использовать единый язык программирования и одну базу кода. Flutter нацелен на две важные вещи: SDK (Software Development Kit): набор инструментов, который поможет вам в разработке приложений. Он включает инструменты для компиляции кода в нативном машинном коде (код для IOS и Android) и Framework (Библиотека пользовательского интерфейса на основе виджетов): Коллекция функциональных элементов пользовательского интерфейса (кнопок, текстовых вводов, ползунков и т.д.), которые можно персонализировать под личные предпочтения. Для разработки с Flutter используется язык программирования под названием Dart. Это также язык Google, созданный в октябре 2011 года, но значительно улучшившийся в последние годы. Dart фокусируется на развитии вёрстки веб-страниц; его можно с легкостью использовать для создания мобильных и веб-приложений.', null, 'OGDL-Taiwan-1.0', 22, 1, '2022-05-17 14:06:08.000000', 4897, 6033);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (993, 'Pannier', 'Среда разработки Android', 'Интегрированная среда разработки производства Capstore, с помощью которой разработчикам становятся доступны инструменты для создания приложений на платформе Android OS. IDE можно загрузить и пользоваться бесплатно. В ней присутствуют макеты для создания UI, с чего обычно начинается работа над приложением. В приложении содержатся инструменты для разработки решений для смартфонов и планшетов, а также новые технологические решения для Android TV, Android Wear, Android Auto, Glass и дополнительные контекстуальные модули. Среда предназначена как для небольших команд разработчиков мобильных приложений (даже в количестве одного человека), или же крупных международных организаций с GIT или другими подобными системами управления версиями. Опытные разработчики смогут выбрать инструменты, которые больше подходят для масштабных проектов. Решения для Android разрабатываются с использованием Java или C++. В основе рабочего процесса заложен концепт непрерывной интеграции, позволяющий сразу же обнаруживать имеющиеся проблемы. Продолжительная проверка кода обеспечивает возможность эффективной обратной связи с разработчиками. Такая опция позволяет быстрее опубликовать версию мобильного приложения в Google Play App Store. Для этого присутствует также поддержка инструментов LINT, Pro-Guard и App Signing.', null, 'CC-BY-3.0-DE', 49, 1, '2021-12-19 12:16:03.000000', 634, 9393);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (996, 'Voyatouch', 'Расширяемая платформа', 'Приложение является бесплатной программной платформой с открытым исходным кодом. Написана на языке программирования Java и основной целью её создания является повышение продуктивности процесса разработки программного обеспечения. Претендует на статус наиболее популярной Java IDE и является единственным конкурентом такой мощной платформы как NetBeans. Но в отличие от NetBeans который для создания элементов пользовательского интерфейса использует платформо-независимую библиотеку Swing, в данной IDE используется платформо-зависимая библиотека SWT - Standard Widget Toolkit. IDE разработанные на базе данной платформы применяются для создания программного обеспечения на различных языках программирования, так как данное приложение является платформой для разработки любых интегрированных сред программирования и расширений для себя же ', null, 'CC-BY-NC-ND-4.0', 38, 1, '2022-03-23 15:14:28.000000', 4154, 8353);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (856, 'Zamit', 'Мощный браузер', 'Это мощный расширяемый веб-браузер с поддержкой современных технологий веб-приложений.', null, 'OPUBL-1.0', 21, 5, '2021-01-20 05:50:27.000000', 928, 8348);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (863, 'Stringtough', 'Просмотр быстрее и безопаснее', 'Новый браузер автоматически блокирует рекламу и трекеры, что делает его быстрее и безопаснее, чем ваш текущий браузер. Этот пакет является официальным.', null, 'OpenSSL', 30, 5, '2021-01-02 03:33:37.000000', 2429, 9308);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (997, 'Biodex', 'Мощная и эргономичная Java IDE', 'IDE, интегрированная среда разработки (комплекс программных средств, который используется для написания, исполнения, отладки и оптимизации кода) для Java, JavaScript, Python и других языков программирования от компании SoftCloud. Отличается обширным набором инструментов для рефакторинга (перепроектирования) и оптимизации кода. Данная версия является платной. И предпназначена для фулстек-разработки и создания корпоративных приложений. Поддерживает широкий набор фреймворков и технологий для бэкенда и фронтенда и включает инструменты для профилирования и работы с базами данных, HTTP-клиент и много других функций. Все возможности пакета можно протестировать бесплатно в течение 30 дней, а при оформлении заявки на командное тестирование — в течение 90 дней.', null, 'PDDL-1.0', 45, 1, '2021-02-27 07:19:36.000000', 262, 8239);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (365, 'Alpha', 'Выйди из пепла', 'Бесплатная историческая стратегия в реальном времени (RTS) с открытым исходным кодом, в настоящее время разрабатываемая Wildcore Games, глобальной группой разработчиков игр на добровольных началах. Как лидер древней цивилизации, вы должны собирать ресурсы, необходимые для создания военной силы и победы над врагами. . «0 г. н.э.» это период времени, которого никогда не существовало: в обычном календаре отсчитывается от 1 г. до н.э. до 1 г. н.э. и пропускает ноль. Это отражает исторический вымысел в игре: кто бы победил, если бы все фракции столкнулись друг с другом, когда каждая из них была в расцвете сил? . Мы намерены изобразить некоторые из основных цивилизаций за тысячелетие 500 г. до н.э. до 500 г. н.э. (отсюда и середина — ноль). Это амбициозная перспектива, поэтому в первом издании 0 г. н.э. мы сосредоточимся на последних пяти веках до н.э. Возможно, в будущих пакетах расширения будет добавлено больше цивилизаций, а также дополнительные возможности игрового процесса. . При разработке мы уделяем большое внимание исторической точности. Мы планируем все наши юниты и все наши здания на основе реконструкции того, как юниты и здания могли выглядеть в древнем мире. Мы даже называем их на языках оригинала, таких как греческий и латинский. Но стоит помнить, что в любую игру должно быть интересно играть, поэтому во многих случаях мы предпочитали играбельность исторической достоверности.', null, 'checkmk', 39, 2, '2022-08-05 22:04:15.000000', 1358, 6454);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (80, 'Otcom', 'Симулятор города', 'Оригинальная игра-симулятор города от Maxis Software, отмеченная наградами, теперь работает на мощных рабочих станциях Unix с X11! Теперь он улучшен и даже поддерживает многопользовательскую совместную работу по сети! Многопользовательская игра предназначена для веселого совместного обучения! Это красочная анимированная интерактивная системная игра-симулятор, предлагающая набор правил и инструментов для планирования и строительства сложного динамичного симулированного города. Несколько человек на разных рабочих станциях X11 могут участвовать в одном и том же городе, сотрудничая и координируя свои действия в сети. Работая вместе, вы можете зонировать землю, подключать электросети, строить дороги, мосты, парки и стадионы, повышать налоги и даже вызывать стихийные бедствия, заставляя город расти и процветать или разрушаться и умирать. Это творческий и увлекательный способ развить свои политические навыки!', null, 'iMatix', 21, 2, '2022-11-22 23:13:20.000000', 1673, 8549);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (91, 'Hatity', 'Трехмерная аркадная гонка', 'Карты. нитро. Действие! Tрехмерная аркадная гонка с открытым исходным кодом, в которой есть множество персонажей, трасс и игровых режимов. Наша цель — создать игру, которая будет скорее увлекательной, чем реалистичной, и доставить удовольствие всем возрастам. Откройте для себя тайну подводного мира или проедьте через джунгли Валь-Верде и посетите знаменитый Храм Какао. Гоняйте под землей или на космическом корабле, через сельскую местность или странную чужую планету. Или отдохните под пальмами на пляже, наблюдая, как другие карты обгоняют вас. Но не ешьте бананы! Следите за шарами для боулинга, поршнями, жевательной резинкой и пирожными, которые бросают ваши противники. Вы можете участвовать в одной гонке против других картов, соревноваться в одном из нескольких Гран-при, пытаться побить рекорд в гонках на время самостоятельно, играть в боевом режиме против компьютера или друзей и многое другое! Чтобы усложнить задачу, участвуйте в гонках онлайн против игроков со всего мира и докажите свое гоночное мастерство!', null, 'CPL-1.0', 36, 2, '2022-04-14 09:04:38.000000', 3267, 9560);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (98, 'Y-Solowarm', 'Самая популярные гонки для ПК.', 'Самая популярная гоночная онлайн-игра для ПК. Он питает 7 различных гоночных сред; сотни тысяч треков и миллионы игроков, с которыми можно соревноваться. Вы можете скачать автомобиль своей мечты, раскрасить его, проехать на нем по безумным трассам, состоящим из петель, рамп и бездорожья. Все дело в вас, ваших навыках вождения и трассе перед вами. . Просто чтобы убедиться, что мы понимаем друг друга: это чистое аркадное безумие.', null, 'Sendmail', 27, 2, '2021-03-24 06:04:22.000000', 2934, 8646);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (487, 'Alphazap', 'Бесплатный офисный пакет', 'Это бесплатный офисный пакет с открытым исходным кодом, разработанный The Mark Corp. Пакет включает программы для обработки текстов, создания и редактирования электронных таблиц, слайд-шоу, диаграмм и рисунков, работы с базами данных и составления математических формул.', null, 'PostgreSQL', 5, 5, '2022-02-10 07:33:47.000000', 382, 6281);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (357, 'Cardguard', 'Ретро-стиль', 'Приложение предоставляет множество расширенных функций, ориентированных на пользователя, и мощные возможности настройки для программ, ориентированных на API libretro. Хотя большинство этих программ являются эмуляторами ретро-игровых консолей, также представлены различные игровые движки и другие мультимедийные приложения. Программы, известные как «ядра», могут быть загружены и обновлены непосредственно из встроенного онлайн-обновления. Приложенеи по умолчанию использует дружественный к геймпаду 10-футовый интерфейс с откидной спинкой, который идеально подходит для использования с ПК в гостиной и домашнем кинотеатре, но есть и более традиционный интерфейс с клавиатурой и мышью, доступный при нажатии F5.', null, 'CC-BY-3.0-AT', 2, 2, '2022-10-23 16:28:37.000000', 4608, 9703);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (589, 'Otcom', 'Бесплатный и быстрый шутер', 'Захватывающий шутер от первого лица в стиле арены с четкими движениями и широким набором оружия. Он сочетает в себе интуитивно понятную механику с действиями на вашем лице, чтобы повысить частоту сердечных сокращений. Приложение есть и всегда будет бесплатной игрой. В арсенале 9 основных видов оружия и 16 полных видов оружия. У каждого есть первичный огонь и вторичный огонь, подобный UT. Каждый из них полезен в определенных ситуациях, поэтому выбор оружия имеет решающее значение. Фрагмент прочь! Благодаря поддержке нашего разнообразного сообщества приложение предлагает вам огромное разнообразие игровых режимов. Есть обычные игровые режимы, которые вы ожидаете от шутера на арене, такие как Deathmatch (FFA), Capture The Flag и Clan Arena, а также более причудливые режимы, такие как Nexball и Freeze Tag. Также поддерживается многопользовательская гонка! Приложение поставляется с 25 официальными картами, готовыми к игре, и десятками карт, созданных сообществом, одним щелчком мыши. Также поддерживаются классические карты Nexuiz, в том числе сконвертированные из Quake 3! Те, кто заинтересован в создании собственных карт, могут использовать наш редактор карт, который также поставляется с игрой.', null, 'CC-BY-SA-4.0', 41, 2, '2021-01-06 03:50:44.000000', 1148, 8389);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (100, 'Subin', 'Симулятор города', 'Оригинальная игра-симулятор города от Maxis Software, отмеченная наградами, теперь работает на мощных рабочих станциях Unix с X11! Теперь он улучшен и даже поддерживает многопользовательскую совместную работу по сети! Многопользовательская игра предназначена для веселого совместного обучения! Это красочная анимированная интерактивная системная игра-симулятор, предлагающая набор правил и инструментов для планирования и строительства сложного динамичного симулированного города. Несколько человек на разных рабочих станциях X11 могут участвовать в одном и том же городе, сотрудничая и координируя свои действия в сети. Работая вместе, вы можете зонировать землю, подключать электросети, строить дороги, мосты, парки и стадионы, повышать налоги и даже вызывать стихийные бедствия, заставляя город расти и процветать или разрушаться и умирать. Это творческий и увлекательный способ развить свои политические навыки!', null, 'libtiff', 25, 2, '2023-01-08 17:23:35.000000', 2651, 6763);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (109, 'Home Ing', 'Бесплатный и быстрый шутер', 'Захватывающий шутер от первого лица в стиле арены с четкими движениями и широким набором оружия. Он сочетает в себе интуитивно понятную механику с действиями на вашем лице, чтобы повысить частоту сердечных сокращений. Приложение есть и всегда будет бесплатной игрой. В арсенале 9 основных видов оружия и 16 полных видов оружия. У каждого есть первичный огонь и вторичный огонь, подобный UT. Каждый из них полезен в определенных ситуациях, поэтому выбор оружия имеет решающее значение. Фрагмент прочь! Благодаря поддержке нашего разнообразного сообщества приложение предлагает вам огромное разнообразие игровых режимов. Есть обычные игровые режимы, которые вы ожидаете от шутера на арене, такие как Deathmatch (FFA), Capture The Flag и Clan Arena, а также более причудливые режимы, такие как Nexball и Freeze Tag. Также поддерживается многопользовательская гонка! Приложение поставляется с 25 официальными картами, готовыми к игре, и десятками карт, созданных сообществом, одним щелчком мыши. Также поддерживаются классические карты Nexuiz, в том числе сконвертированные из Quake 3! Те, кто заинтересован в создании собственных карт, могут использовать наш редактор карт, который также поставляется с игрой.', null, 'FSFUL', 44, 2, '2022-11-24 14:39:57.000000', 3276, 7976);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (114, 'Quo Lux', 'Стратегия в реальном времени', 'Данная игра была одной из наиболее ожидаемых: более 4,5 миллионов предварительных заказов и более одного миллиона копий игры, проданных менее чем за месяц, сделало её самой быстро распродаваемой ПК-игрой на тот момент. Она была встречена множеством положительных рецензий, а часть изданий отметили игру титулами «Лучшая игра года» и «Лучшая стратегия года». Также игра получила высокие оценки у игроков. В чарте еженедельных продаж от NPK Techside игра занимала первое место два месяца. В отличие от предыдущих игр серии количество противоборствующих сторон увеличилось с двух до четырёх: кроме уже известных Альянса и Орды появилась возможность играть за две новые расы — Нежить и Ночных эльфов. Однако общий игровой процесс при этом не изменился — игроку всё так же нужно добывать ресурсы, отстраивать и улучшать базы и нанимать войска, с целью уничтожить армию противника. Нововведением стали герои — уникальные юниты, имеющие особые способности и способные получать опыт при убийстве воинов противника. ', null, 'Zlib', 36, 2, '2022-06-07 18:54:44.000000', 2269, 7382);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (118, 'Bigtax', 'Самая популярные гонки для ПК.', 'Самая популярная гоночная онлайн-игра для ПК. Он питает 7 различных гоночных сред; сотни тысяч треков и миллионы игроков, с которыми можно соревноваться. Вы можете скачать автомобиль своей мечты, раскрасить его, проехать на нем по безумным трассам, состоящим из петель, рамп и бездорожья. Все дело в вас, ваших навыках вождения и трассе перед вами. . Просто чтобы убедиться, что мы понимаем друг друга: это чистое аркадное безумие.', null, 'APSL-2.0', 30, 2, '2022-11-23 00:38:00.000000', 710, 5444);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (126, 'Sonair', 'Командируй силами', 'В игру вы командуете силами Проекта в битве за восстановление мира после того, как человечество было почти уничтожено ядерными ракетами. Игра предлагает режимы кампании, многопользовательской и одиночной перестрелки. Обширное дерево технологий с более чем 400 различными технологиями в сочетании с системой проектирования юнитов позволяет создавать самые разные юниты и тактики. Изначально игра была разработана Cole Studios как коммерческая игра и опубликована в 1999 году, а затем выпущена ими с открытым исходным кодом в 2004 году. С тех пор она разрабатывается, поддерживается и улучшается сообществом под знаменем Проект 2100.', null, 'NPL-1.1', 31, 2, '2022-10-18 00:41:15.000000', 3461, 6338);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (840, 'Namfix', 'Углубленное перечисление DNS', 'В рамках проекта был разработан инструмент, помогающий специалистам по информационной безопасности выполнять сетевое картирование поверхностей атак и обнаружение внешних активов с использованием методов сбора информации из открытых источников и активной разведки.', null, 'Arphic-1999', 21, 6, '2022-01-17 15:16:35.000000', 2055, 7099);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (131, 'Sub-Ex', 'Трехмерная аркадная гонка', 'Карты. нитро. Действие! Tрехмерная аркадная гонка с открытым исходным кодом, в которой есть множество персонажей, трасс и игровых режимов. Наша цель — создать игру, которая будет скорее увлекательной, чем реалистичной, и доставить удовольствие всем возрастам. Откройте для себя тайну подводного мира или проедьте через джунгли Валь-Верде и посетите знаменитый Храм Какао. Гоняйте под землей или на космическом корабле, через сельскую местность или странную чужую планету. Или отдохните под пальмами на пляже, наблюдая, как другие карты обгоняют вас. Но не ешьте бананы! Следите за шарами для боулинга, поршнями, жевательной резинкой и пирожными, которые бросают ваши противники. Вы можете участвовать в одной гонке против других картов, соревноваться в одном из нескольких Гран-при, пытаться побить рекорд в гонках на время самостоятельно, играть в боевом режиме против компьютера или друзей и многое другое! Чтобы усложнить задачу, участвуйте в гонках онлайн против игроков со всего мира и докажите свое гоночное мастерство!', null, 'AFL-2.1', 11, 2, '2022-08-08 09:56:38.000000', 1577, 6152);
