INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (137, 'Sonair', 'Ретро-стиль', 'Приложение предоставляет множество расширенных функций, ориентированных на пользователя, и мощные возможности настройки для программ, ориентированных на API libretro. Хотя большинство этих программ являются эмуляторами ретро-игровых консолей, также представлены различные игровые движки и другие мультимедийные приложения. Программы, известные как «ядра», могут быть загружены и обновлены непосредственно из встроенного онлайн-обновления. Приложенеи по умолчанию использует дружественный к геймпаду 10-футовый интерфейс с откидной спинкой, который идеально подходит для использования с ПК в гостиной и домашнем кинотеатре, но есть и более традиционный интерфейс с клавиатурой и мышью, доступный при нажатии F5.', null, 'OLDAP-2.4', 32, 2, '2022-09-09 04:25:21.000000', 1633, 8288);
