INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (262, 'Kanlam', '2D космический шутер', '2D космический шутер с потрясающими визуальными эффектами и привлекательной физикой. Игроки могут сражаться друг с другом или с врагами, управляемыми компьютером, в захватывающих игровых режимах. Сюжет: В 3547 году цивилизации по всей галактике заселили свои планеты, живя в мире и гармонии с окружающей средой. Но за пределами созерцательных мест обитания бушует ВЕЛИКАЯ ВОЙНА. Как известный боец ​​на пути к нескончаемой славе и процветанию, вы должны защитить свою планету от надвигающейся гибели ваших завистливых соседей!', null, 'Sendmail-8.23', 44, 2, '2022-01-21 21:21:31.000000', 3398, 6129);
