INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (233, 'Job', 'Портативный пасьянс', 'Пасья́нс (фр. patience — терпение) — карточная игра для одного человека. Играющий раскладывает карты, придерживаясь определённых правил и, чаще всего, преследуя некоторую цель. В зависимости от правил цель может быть достижима в той или иной степени благодаря интеллектуальным усилиям играющего и благодаря случайности (зависящей от расклада). Последнее позволяет использовать пасьянсы для гадания. ', null, 'CNRI-Python-GPL-Compatible', 14, 2, '2022-04-14 01:26:23.000000', 2762, 5209);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (236, 'Matsoft', 'Командируй силами', 'В игру вы командуете силами Проекта в битве за восстановление мира после того, как человечество было почти уничтожено ядерными ракетами. Игра предлагает режимы кампании, многопользовательской и одиночной перестрелки. Обширное дерево технологий с более чем 400 различными технологиями в сочетании с системой проектирования юнитов позволяет создавать самые разные юниты и тактики. Изначально игра была разработана Cole Studios как коммерческая игра и опубликована в 1999 году, а затем выпущена ими с открытым исходным кодом в 2004 году. С тех пор она разрабатывается, поддерживается и улучшается сообществом под знаменем Проект 2100.', null, 'AFL-2.0', 45, 2, '2022-12-16 10:12:16.000000', 366, 9799);
