INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (289, 'Bigtax', 'Бесплатный и быстрый шутер', 'Захватывающий шутер от первого лица в стиле арены с четкими движениями и широким набором оружия. Он сочетает в себе интуитивно понятную механику с действиями на вашем лице, чтобы повысить частоту сердечных сокращений. Приложение есть и всегда будет бесплатной игрой. В арсенале 9 основных видов оружия и 16 полных видов оружия. У каждого есть первичный огонь и вторичный огонь, подобный UT. Каждый из них полезен в определенных ситуациях, поэтому выбор оружия имеет решающее значение. Фрагмент прочь! Благодаря поддержке нашего разнообразного сообщества приложение предлагает вам огромное разнообразие игровых режимов. Есть обычные игровые режимы, которые вы ожидаете от шутера на арене, такие как Deathmatch (FFA), Capture The Flag и Clan Arena, а также более причудливые режимы, такие как Nexball и Freeze Tag. Также поддерживается многопользовательская гонка! Приложение поставляется с 25 официальными картами, готовыми к игре, и десятками карт, созданных сообществом, одним щелчком мыши. Также поддерживаются классические карты Nexuiz, в том числе сконвертированные из Quake 3! Те, кто заинтересован в создании собственных карт, могут использовать наш редактор карт, который также поставляется с игрой.', null, 'Knuth-CTAN', 40, 2, '2022-01-31 05:15:57.000000', 3261, 9558);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (291, 'Alphazap', 'Трехмерная аркадная гонка', 'Карты. нитро. Действие! Tрехмерная аркадная гонка с открытым исходным кодом, в которой есть множество персонажей, трасс и игровых режимов. Наша цель — создать игру, которая будет скорее увлекательной, чем реалистичной, и доставить удовольствие всем возрастам. Откройте для себя тайну подводного мира или проедьте через джунгли Валь-Верде и посетите знаменитый Храм Какао. Гоняйте под землей или на космическом корабле, через сельскую местность или странную чужую планету. Или отдохните под пальмами на пляже, наблюдая, как другие карты обгоняют вас. Но не ешьте бананы! Следите за шарами для боулинга, поршнями, жевательной резинкой и пирожными, которые бросают ваши противники. Вы можете участвовать в одной гонке против других картов, соревноваться в одном из нескольких Гран-при, пытаться побить рекорд в гонках на время самостоятельно, играть в боевом режиме против компьютера или друзей и многое другое! Чтобы усложнить задачу, участвуйте в гонках онлайн против игроков со всего мира и докажите свое гоночное мастерство!', null, 'GPL-2.0-only', 11, 2, '2021-06-04 02:09:31.000000', 4197, 7357);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (845, 'Holdlamis', 'Анонимный VPN', 'Это простой, быстрый и безопасный VPN-сервис. Приложение не требует учетной записи пользователя, не ведет журналы и не отслеживает вас каким-либо образом. Услуга полностью оплачивается за счет пожертвований таких пользователей, как вы.', null, 'MIT-enna', 31, 6, '2021-01-25 12:04:39.000000', 4808, 9973);
