INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (507, 'Subin', 'Ретро-стиль', 'Приложение предоставляет множество расширенных функций, ориентированных на пользователя, и мощные возможности настройки для программ, ориентированных на API libretro. Хотя большинство этих программ являются эмуляторами ретро-игровых консолей, также представлены различные игровые движки и другие мультимедийные приложения. Программы, известные как «ядра», могут быть загружены и обновлены непосредственно из встроенного онлайн-обновления. Приложенеи по умолчанию использует дружественный к геймпаду 10-футовый интерфейс с откидной спинкой, который идеально подходит для использования с ПК в гостиной и домашнем кинотеатре, но есть и более традиционный интерфейс с клавиатурой и мышью, доступный при нажатии F5.', null, 'Rdisc', 7, 2, '2023-01-02 06:15:26.000000', 2427, 5115);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (565, 'Veribet', 'Выйди из пепла', 'Бесплатная историческая стратегия в реальном времени (RTS) с открытым исходным кодом, в настоящее время разрабатываемая Wildcore Games, глобальной группой разработчиков игр на добровольных началах. Как лидер древней цивилизации, вы должны собирать ресурсы, необходимые для создания военной силы и победы над врагами. . «0 г. н.э.» это период времени, которого никогда не существовало: в обычном календаре отсчитывается от 1 г. до н.э. до 1 г. н.э. и пропускает ноль. Это отражает исторический вымысел в игре: кто бы победил, если бы все фракции столкнулись друг с другом, когда каждая из них была в расцвете сил? . Мы намерены изобразить некоторые из основных цивилизаций за тысячелетие 500 г. до н.э. до 500 г. н.э. (отсюда и середина — ноль). Это амбициозная перспектива, поэтому в первом издании 0 г. н.э. мы сосредоточимся на последних пяти веках до н.э. Возможно, в будущих пакетах расширения будет добавлено больше цивилизаций, а также дополнительные возможности игрового процесса. . При разработке мы уделяем большое внимание исторической точности. Мы планируем все наши юниты и все наши здания на основе реконструкции того, как юниты и здания могли выглядеть в древнем мире. Мы даже называем их на языках оригинала, таких как греческий и латинский. Но стоит помнить, что в любую игру должно быть интересно играть, поэтому во многих случаях мы предпочитали играбельность исторической достоверности.', null, 'Parity-6.0.0', 9, 2, '2023-02-22 05:53:49.000000', 2716, 7097);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (521, 'Holdlamis', 'Трехмерная аркадная гонка', 'Карты. нитро. Действие! Tрехмерная аркадная гонка с открытым исходным кодом, в которой есть множество персонажей, трасс и игровых режимов. Наша цель — создать игру, которая будет скорее увлекательной, чем реалистичной, и доставить удовольствие всем возрастам. Откройте для себя тайну подводного мира или проедьте через джунгли Валь-Верде и посетите знаменитый Храм Какао. Гоняйте под землей или на космическом корабле, через сельскую местность или странную чужую планету. Или отдохните под пальмами на пляже, наблюдая, как другие карты обгоняют вас. Но не ешьте бананы! Следите за шарами для боулинга, поршнями, жевательной резинкой и пирожными, которые бросают ваши противники. Вы можете участвовать в одной гонке против других картов, соревноваться в одном из нескольких Гран-при, пытаться побить рекорд в гонках на время самостоятельно, играть в боевом режиме против компьютера или друзей и многое другое! Чтобы усложнить задачу, участвуйте в гонках онлайн против игроков со всего мира и докажите свое гоночное мастерство!', null, 'LAL-1.2', 39, 2, '2021-09-15 15:05:04.000000', 2709, 8370);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (668, 'Domainer', 'Самая популярные гонки для ПК.', 'Самая популярная гоночная онлайн-игра для ПК. Он питает 7 различных гоночных сред; сотни тысяч треков и миллионы игроков, с которыми можно соревноваться. Вы можете скачать автомобиль своей мечты, раскрасить его, проехать на нем по безумным трассам, состоящим из петель, рамп и бездорожья. Все дело в вас, ваших навыках вождения и трассе перед вами. . Просто чтобы убедиться, что мы понимаем друг друга: это чистое аркадное безумие.', null, 'NPL-1.0', 30, 2, '2022-05-25 19:03:39.000000', 3626, 8219);
