INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (596, 'Vagram', 'Командируй силами', 'В игру вы командуете силами Проекта в битве за восстановление мира после того, как человечество было почти уничтожено ядерными ракетами. Игра предлагает режимы кампании, многопользовательской и одиночной перестрелки. Обширное дерево технологий с более чем 400 различными технологиями в сочетании с системой проектирования юнитов позволяет создавать самые разные юниты и тактики. Изначально игра была разработана Cole Studios как коммерческая игра и опубликована в 1999 году, а затем выпущена ими с открытым исходным кодом в 2004 году. С тех пор она разрабатывается, поддерживается и улучшается сообществом под знаменем Проект 2100.', null, 'SMPPL', 8, 2, '2022-11-10 02:55:03.000000', 1663, 8589);
