INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (713, 'Stringtough', 'Портативный пасьянс', 'Пасья́нс (фр. patience — терпение) — карточная игра для одного человека. Играющий раскладывает карты, придерживаясь определённых правил и, чаще всего, преследуя некоторую цель. В зависимости от правил цель может быть достижима в той или иной степени благодаря интеллектуальным усилиям играющего и благодаря случайности (зависящей от расклада). Последнее позволяет использовать пасьянсы для гадания. ', null, 'CC-BY-NC-SA-3.0-DE', 27, 2, '2022-04-26 06:09:38.000000', 3189, 8438);
