INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (754, 'Otcom', 'Стратегия в реальном времени', 'Данная игра была одной из наиболее ожидаемых: более 4,5 миллионов предварительных заказов и более одного миллиона копий игры, проданных менее чем за месяц, сделало её самой быстро распродаваемой ПК-игрой на тот момент. Она была встречена множеством положительных рецензий, а часть изданий отметили игру титулами «Лучшая игра года» и «Лучшая стратегия года». Также игра получила высокие оценки у игроков. В чарте еженедельных продаж от NPK Techside игра занимала первое место два месяца. В отличие от предыдущих игр серии количество противоборствующих сторон увеличилось с двух до четырёх: кроме уже известных Альянса и Орды появилась возможность играть за две новые расы — Нежить и Ночных эльфов. Однако общий игровой процесс при этом не изменился — игроку всё так же нужно добывать ресурсы, отстраивать и улучшать базы и нанимать войска, с целью уничтожить армию противника. Нововведением стали герои — уникальные юниты, имеющие особые способности и способные получать опыт при убийстве воинов противника. ', null, 'GPL-3.0-or-later', 8, 2, '2021-11-03 16:09:02.000000', 4550, 7800);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (820, 'Gembucket', 'Симулятор города', 'Оригинальная игра-симулятор города от Maxis Software, отмеченная наградами, теперь работает на мощных рабочих станциях Unix с X11! Теперь он улучшен и даже поддерживает многопользовательскую совместную работу по сети! Многопользовательская игра предназначена для веселого совместного обучения! Это красочная анимированная интерактивная системная игра-симулятор, предлагающая набор правил и инструментов для планирования и строительства сложного динамичного симулированного города. Несколько человек на разных рабочих станциях X11 могут участвовать в одном и том же городе, сотрудничая и координируя свои действия в сети. Работая вместе, вы можете зонировать землю, подключать электросети, строить дороги, мосты, парки и стадионы, повышать налоги и даже вызывать стихийные бедствия, заставляя город расти и процветать или разрушаться и умирать. Это творческий и увлекательный способ развить свои политические навыки!', null, 'NCSA', 37, 2, '2023-02-17 14:31:30.000000', 4073, 7474);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (826, 'Otcom', 'Командируй силами', 'В игру вы командуете силами Проекта в битве за восстановление мира после того, как человечество было почти уничтожено ядерными ракетами. Игра предлагает режимы кампании, многопользовательской и одиночной перестрелки. Обширное дерево технологий с более чем 400 различными технологиями в сочетании с системой проектирования юнитов позволяет создавать самые разные юниты и тактики. Изначально игра была разработана Cole Studios как коммерческая игра и опубликована в 1999 году, а затем выпущена ими с открытым исходным кодом в 2004 году. С тех пор она разрабатывается, поддерживается и улучшается сообществом под знаменем Проект 2100.', null, 'AFL-2.0', 4, 2, '2022-02-13 08:45:52.000000', 4542, 7531);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (834, 'Hatity', 'Стратегия в реальном времени', 'Данная игра была одной из наиболее ожидаемых: более 4,5 миллионов предварительных заказов и более одного миллиона копий игры, проданных менее чем за месяц, сделало её самой быстро распродаваемой ПК-игрой на тот момент. Она была встречена множеством положительных рецензий, а часть изданий отметили игру титулами «Лучшая игра года» и «Лучшая стратегия года». Также игра получила высокие оценки у игроков. В чарте еженедельных продаж от NPK Techside игра занимала первое место два месяца. В отличие от предыдущих игр серии количество противоборствующих сторон увеличилось с двух до четырёх: кроме уже известных Альянса и Орды появилась возможность играть за две новые расы — Нежить и Ночных эльфов. Однако общий игровой процесс при этом не изменился — игроку всё так же нужно добывать ресурсы, отстраивать и улучшать базы и нанимать войска, с целью уничтожить армию противника. Нововведением стали герои — уникальные юниты, имеющие особые способности и способные получать опыт при убийстве воинов противника. ', null, 'XFree86-1.1', 9, 2, '2021-07-14 20:14:44.000000', 2817, 5337);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (842, 'Sonsing', '2D космический шутер', '2D космический шутер с потрясающими визуальными эффектами и привлекательной физикой. Игроки могут сражаться друг с другом или с врагами, управляемыми компьютером, в захватывающих игровых режимах. Сюжет: В 3547 году цивилизации по всей галактике заселили свои планеты, живя в мире и гармонии с окружающей средой. Но за пределами созерцательных мест обитания бушует ВЕЛИКАЯ ВОЙНА. Как известный боец ​​на пути к нескончаемой славе и процветанию, вы должны защитить свою планету от надвигающейся гибели ваших завистливых соседей!', null, 'SMPPL', 3, 2, '2022-08-13 11:22:34.000000', 352, 7732);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (335, 'Tempsoft', 'Пакет для создания 3D', 'Это бесплатный пакет для создания 3D с открытым исходным кодом. Он поддерживает весь 3D-конвейер — моделирование, оснастку, анимацию, симуляцию, рендеринг, композитинг и отслеживание движения, а также редактирование видео. Являяся публичным проектом, созданный сотнями людей со всего мира; студиями и отдельными художниками, профессионалами и любителями, учеными, студентами, экспертами по визуальным эффектам, аниматорами, игровыми художниками, моддерами, приложение стало must-have для дизайнеров.', null, 'CDL-1.0', 8, 3, '2022-04-26 09:55:20.000000', 2063, 6405);
