INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (874, 'Cardguard', 'Стратегия в реальном времени', 'Данная игра была одной из наиболее ожидаемых: более 4,5 миллионов предварительных заказов и более одного миллиона копий игры, проданных менее чем за месяц, сделало её самой быстро распродаваемой ПК-игрой на тот момент. Она была встречена множеством положительных рецензий, а часть изданий отметили игру титулами «Лучшая игра года» и «Лучшая стратегия года». Также игра получила высокие оценки у игроков. В чарте еженедельных продаж от NPK Techside игра занимала первое место два месяца. В отличие от предыдущих игр серии количество противоборствующих сторон увеличилось с двух до четырёх: кроме уже известных Альянса и Орды появилась возможность играть за две новые расы — Нежить и Ночных эльфов. Однако общий игровой процесс при этом не изменился — игроку всё так же нужно добывать ресурсы, отстраивать и улучшать базы и нанимать войска, с целью уничтожить армию противника. Нововведением стали герои — уникальные юниты, имеющие особые способности и способные получать опыт при убийстве воинов противника. ', null, 'Interbase-1.0', 12, 2, '2022-12-17 11:04:42.000000', 1388, 8057);
