INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (881, 'Ronstring', 'Трехмерная аркадная гонка', 'Карты. нитро. Действие! Tрехмерная аркадная гонка с открытым исходным кодом, в которой есть множество персонажей, трасс и игровых режимов. Наша цель — создать игру, которая будет скорее увлекательной, чем реалистичной, и доставить удовольствие всем возрастам. Откройте для себя тайну подводного мира или проедьте через джунгли Валь-Верде и посетите знаменитый Храм Какао. Гоняйте под землей или на космическом корабле, через сельскую местность или странную чужую планету. Или отдохните под пальмами на пляже, наблюдая, как другие карты обгоняют вас. Но не ешьте бананы! Следите за шарами для боулинга, поршнями, жевательной резинкой и пирожными, которые бросают ваши противники. Вы можете участвовать в одной гонке против других картов, соревноваться в одном из нескольких Гран-при, пытаться побить рекорд в гонках на время самостоятельно, играть в боевом режиме против компьютера или друзей и многое другое! Чтобы усложнить задачу, участвуйте в гонках онлайн против игроков со всего мира и докажите свое гоночное мастерство!', null, 'NLOD-2.0', 44, 2, '2022-01-10 22:28:38.000000', 3595, 5252);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (883, 'Sonsing', 'Портативный пасьянс', 'Пасья́нс (фр. patience — терпение) — карточная игра для одного человека. Играющий раскладывает карты, придерживаясь определённых правил и, чаще всего, преследуя некоторую цель. В зависимости от правил цель может быть достижима в той или иной степени благодаря интеллектуальным усилиям играющего и благодаря случайности (зависящей от расклада). Последнее позволяет использовать пасьянсы для гадания. ', null, 'APL-1.0', 48, 2, '2021-10-03 04:16:42.000000', 4562, 7706);
