INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (929, 'Greenlam', 'Бесплатный и быстрый шутер', 'Захватывающий шутер от первого лица в стиле арены с четкими движениями и широким набором оружия. Он сочетает в себе интуитивно понятную механику с действиями на вашем лице, чтобы повысить частоту сердечных сокращений. Приложение есть и всегда будет бесплатной игрой. В арсенале 9 основных видов оружия и 16 полных видов оружия. У каждого есть первичный огонь и вторичный огонь, подобный UT. Каждый из них полезен в определенных ситуациях, поэтому выбор оружия имеет решающее значение. Фрагмент прочь! Благодаря поддержке нашего разнообразного сообщества приложение предлагает вам огромное разнообразие игровых режимов. Есть обычные игровые режимы, которые вы ожидаете от шутера на арене, такие как Deathmatch (FFA), Capture The Flag и Clan Arena, а также более причудливые режимы, такие как Nexball и Freeze Tag. Также поддерживается многопользовательская гонка! Приложение поставляется с 25 официальными картами, готовыми к игре, и десятками карт, созданных сообществом, одним щелчком мыши. Также поддерживаются классические карты Nexuiz, в том числе сконвертированные из Quake 3! Те, кто заинтересован в создании собственных карт, могут использовать наш редактор карт, который также поставляется с игрой.', null, 'GFDL-1.2-invariants-only', 19, 2, '2022-09-03 01:13:10.000000', 4961, 5247);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (957, 'Asoka', 'Ретро-стиль', 'Приложение предоставляет множество расширенных функций, ориентированных на пользователя, и мощные возможности настройки для программ, ориентированных на API libretro. Хотя большинство этих программ являются эмуляторами ретро-игровых консолей, также представлены различные игровые движки и другие мультимедийные приложения. Программы, известные как «ядра», могут быть загружены и обновлены непосредственно из встроенного онлайн-обновления. Приложенеи по умолчанию использует дружественный к геймпаду 10-футовый интерфейс с откидной спинкой, который идеально подходит для использования с ПК в гостиной и домашнем кинотеатре, но есть и более традиционный интерфейс с клавиатурой и мышью, доступный при нажатии F5.', null, 'GFDL-1.3-or-later', 23, 2, '2021-03-02 11:08:20.000000', 2605, 9437);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (495, 'Sonsing', 'Простые, но умные заметки', 'Это небольшое приложение, которое вы можете использовать в качестве приложения для быстрых заметок или задач. Маленький и простой, все внимание сосредоточено на тексте, который вы печатаете. Сохраняет на лету, нет необходимости сохранять вручную. Автоматический отступ. Складывание заметок. Используйте флажки, чтобы отслеживать свои TODO. Заменяет общий синтаксис символами, такими как стрелки. Темная и светлая тема. Портативный режим поддерживается. Бесплатно', null, 'LPL-1.0', 39, 5, '2021-12-01 15:38:04.000000', 4583, 9890);
