INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (952, 'Stringtough', '2D космический шутер', '2D космический шутер с потрясающими визуальными эффектами и привлекательной физикой. Игроки могут сражаться друг с другом или с врагами, управляемыми компьютером, в захватывающих игровых режимах. Сюжет: В 3547 году цивилизации по всей галактике заселили свои планеты, живя в мире и гармонии с окружающей средой. Но за пределами созерцательных мест обитания бушует ВЕЛИКАЯ ВОЙНА. Как известный боец ​​на пути к нескончаемой славе и процветанию, вы должны защитить свою планету от надвигающейся гибели ваших завистливых соседей!', null, 'CC-BY-NC-3.0', 47, 2, '2021-10-03 05:03:31.000000', 1546, 5569);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (955, 'Kanlam', 'Выйди из пепла', 'Бесплатная историческая стратегия в реальном времени (RTS) с открытым исходным кодом, в настоящее время разрабатываемая Wildcore Games, глобальной группой разработчиков игр на добровольных началах. Как лидер древней цивилизации, вы должны собирать ресурсы, необходимые для создания военной силы и победы над врагами. . «0 г. н.э.» это период времени, которого никогда не существовало: в обычном календаре отсчитывается от 1 г. до н.э. до 1 г. н.э. и пропускает ноль. Это отражает исторический вымысел в игре: кто бы победил, если бы все фракции столкнулись друг с другом, когда каждая из них была в расцвете сил? . Мы намерены изобразить некоторые из основных цивилизаций за тысячелетие 500 г. до н.э. до 500 г. н.э. (отсюда и середина — ноль). Это амбициозная перспектива, поэтому в первом издании 0 г. н.э. мы сосредоточимся на последних пяти веках до н.э. Возможно, в будущих пакетах расширения будет добавлено больше цивилизаций, а также дополнительные возможности игрового процесса. . При разработке мы уделяем большое внимание исторической точности. Мы планируем все наши юниты и все наши здания на основе реконструкции того, как юниты и здания могли выглядеть в древнем мире. Мы даже называем их на языках оригинала, таких как греческий и латинский. Но стоит помнить, что в любую игру должно быть интересно играть, поэтому во многих случаях мы предпочитали играбельность исторической достоверности.', null, 'CC-BY-NC-SA-2.0', 10, 2, '2021-04-29 03:19:19.000000', 4346, 9749);
