INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (154, 'Overhold', 'Утилита для обнаружения сети', 'Эта утилита для исследования сети или аудита безопасности. Он поддерживает ping-сканирование (определение работающих хостов), множество методов сканирования портов, определение версии (определение сервисных протоколов и версий приложений, прослушивающих порты) и снятие отпечатков пальцев TCP/IP (идентификация ОС удаленного хоста или устройства). Также предлагает гибкую спецификацию целей и портов, сканирование приманок/невидимок, сканирование sunRPC и многое другое. Большинство платформ Unix и Windows поддерживаются как в режимах графического интерфейса пользователя, так и в режимах командной строки.', null, 'GFDL-1.1-invariants-only', 23, 6, '2022-02-18 07:57:56.000000', 712, 5039);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (975, 'Zaam-Dox', 'Выйди из пепла', 'Бесплатная историческая стратегия в реальном времени (RTS) с открытым исходным кодом, в настоящее время разрабатываемая Wildcore Games, глобальной группой разработчиков игр на добровольных началах. Как лидер древней цивилизации, вы должны собирать ресурсы, необходимые для создания военной силы и победы над врагами. . «0 г. н.э.» это период времени, которого никогда не существовало: в обычном календаре отсчитывается от 1 г. до н.э. до 1 г. н.э. и пропускает ноль. Это отражает исторический вымысел в игре: кто бы победил, если бы все фракции столкнулись друг с другом, когда каждая из них была в расцвете сил? . Мы намерены изобразить некоторые из основных цивилизаций за тысячелетие 500 г. до н.э. до 500 г. н.э. (отсюда и середина — ноль). Это амбициозная перспектива, поэтому в первом издании 0 г. н.э. мы сосредоточимся на последних пяти веках до н.э. Возможно, в будущих пакетах расширения будет добавлено больше цивилизаций, а также дополнительные возможности игрового процесса. . При разработке мы уделяем большое внимание исторической точности. Мы планируем все наши юниты и все наши здания на основе реконструкции того, как юниты и здания могли выглядеть в древнем мире. Мы даже называем их на языках оригинала, таких как греческий и латинский. Но стоит помнить, что в любую игру должно быть интересно играть, поэтому во многих случаях мы предпочитали играбельность исторической достоверности.', null, 'Wsuipa', 38, 2, '2022-11-20 06:31:57.000000', 1321, 9424);
