INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (1000, 'Hatity', 'Симулятор города', 'Оригинальная игра-симулятор города от Maxis Software, отмеченная наградами, теперь работает на мощных рабочих станциях Unix с X11! Теперь он улучшен и даже поддерживает многопользовательскую совместную работу по сети! Многопользовательская игра предназначена для веселого совместного обучения! Это красочная анимированная интерактивная системная игра-симулятор, предлагающая набор правил и инструментов для планирования и строительства сложного динамичного симулированного города. Несколько человек на разных рабочих станциях X11 могут участвовать в одном и том же городе, сотрудничая и координируя свои действия в сети. Работая вместе, вы можете зонировать землю, подключать электросети, строить дороги, мосты, парки и стадионы, повышать налоги и даже вызывать стихийные бедствия, заставляя город расти и процветать или разрушаться и умирать. Это творческий и увлекательный способ развить свои политические навыки!', null, 'TMate', 25, 2, '2021-11-06 23:16:30.000000', 3484, 7519);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (469, 'Gembucket', 'Канбан с открытым исходным кодом', 'Это приложение для совместной работы с канбан-досками с открытым исходным кодом. Независимо от того, ведете ли вы личный список дел, планируете отпуск с друзьями или работаете в команде над своей следующей революционной идеей, доски Канбан — непревзойденный инструмент для организации ваших дел. Они дают вам визуальный обзор текущего состояния вашего проекта и повышают вашу продуктивность, позволяя сосредоточиться на нескольких наиболее важных элементах. В зависимости от целевой среды может потребоваться изменение некоторых параметров конфигурации. Для получения полного списка опций конфигурации ознакомьтесь на сайте', null, 'TU-Berlin-2.0', 18, 5, '2022-10-30 00:53:34.000000', 1496, 8919);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (21, 'Asoka', 'Редактор векторной графики', 'Бесплатный редактор векторной графики с открытым исходным кодом. Он предлагает богатый набор функций и широко используется как для художественных, так и для технических иллюстраций, таких как мультфильмы, картинки, логотипы, типографика, диаграммы и блок-схемы. Он использует векторную графику, чтобы обеспечить четкие распечатки и визуализацию с неограниченным разрешением и не привязан к фиксированному количеству пикселей, как растровая графика. Использует стандартизированный формат файла SVG в качестве основного формата, который поддерживается многими другими приложениями, включая веб-браузеры. Поддерживает множество расширенных функций SVG (маркеры, клоны, альфа-смешивание и т. д.), и большое внимание уделяется разработке оптимизированного интерфейса. Очень легко редактировать узлы, выполнять сложные операции с путями, отслеживать растровые изображения и многое другое. Мы также стремимся поддерживать процветающее сообщество пользователей и разработчиков, используя открытую разработку, ориентированную на сообщество.', null, 'Newsletr', 50, 3, '2021-04-09 04:54:21.000000', 1654, 5587);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (25, 'Y-Solowarm', 'Пакет для создания 3D', 'Это бесплатный пакет для создания 3D с открытым исходным кодом. Он поддерживает весь 3D-конвейер — моделирование, оснастку, анимацию, симуляцию, рендеринг, композитинг и отслеживание движения, а также редактирование видео. Являяся публичным проектом, созданный сотнями людей со всего мира; студиями и отдельными художниками, профессионалами и любителями, учеными, студентами, экспертами по визуальным эффектам, аниматорами, игровыми художниками, моддерами, приложение стало must-have для дизайнеров.', null, 'NGPL', 16, 3, '2021-09-07 20:24:05.000000', 4242, 6424);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (27, 'Holdlamis', '3D CAD', 'Это программное обеспечение для создания твердых 3D-объектов САПР. Он фокусируется на аспектах САПР, а не на художественных аспектах. Приложение не является интерактивным средством моделирования. Вместо этого это что-то вроде 3D-компилятора, который считывает файл сценария, описывающий объект, и рендерит 3D-модель из этого сценария. Это дает разработчику полный контроль над процессом моделирования и позволяет ему легко изменять любой шаг в процессе моделирования или создавать конструкции, определяемые настраиваемыми параметрами. ПРИМЕЧАНИЕ. Это моментальный снимок разработки, автоматически созданный из исходного репозитория.', null, 'bzip2-1.0.6', 44, 3, '2021-10-20 17:49:34.000000', 1935, 5369);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (84, 'Konklux', 'Цифровое творчество', 'В приложении есть все традиционные инструменты, которые вы любите из других пакетов для цифровой живописи и редактирования изображений, такие как инструмент «Волшебная палочка», мощные инструменты выделения и высококачественные кисти. Но данный софт добавляет к этой смеси свою изюминку. Ваши инструменты могут не только наносить цвет, но также дополнительно материалы и неровности. Это дает изображениям, которые вы создаете, 3D-атрибуты, которые визуализируются в реальном времени на вашем графическом процессоре с использованием света и теней, что приводит к потрясающим и уникальным изображениям. Возьмем, к примеру, прикрепленный скриншот «Каменного великана». Он был окрашен светоотражающими материалами, выпуклостями и освещен двумя источниками света. Вы можете свободно перемещать источники света, чтобы увидеть, как это меняет настроение, освещение и тени сцены. Материалы и источники света в приложении используют самые современные технологии, такие как PBR и GGX.', null, '0BSD', 39, 3, '2021-01-10 06:49:55.000000', 3218, 5755);
