INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (120, 'Latlux', 'Бесплатный профессиональный редактор изображений', 'Бесплатный профессиональный редактор изображений с полным ускорением графического процессора. Поддерживает следующий функционал: слои, маски, стили слоя, фильтры и слои интерактивных фильтров, поддержка HDR, поддержка графического планшета, полное ускорение графического процессора.', null, 'Borceux', 24, 3, '2022-06-20 22:57:46.000000', 4933, 6254);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (122, 'Bytecard', 'Средство параметрического 3D-моделирования', 'Программа для параметрического 3D-моделирования. Параметрическое моделирование позволяет легко модифицировать проект, возвращаясь к истории модели и изменяя ее параметры. Приложение имеет открытый исходный код и полностью модульный, что позволяет использовать расширенные возможности расширения и настройки. Приложение является мультиплатформенным и читает и записывает многие открытые форматы файлов, такие как STEP, IGES, STL и другие.', null, 'blessing', 20, 3, '2023-02-23 12:44:59.000000', 617, 5262);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (156, 'Pannier', 'Видеоредактор для создания GIF', 'Это видеоредактор с открытым исходным кодом, построенный на Haskell, для создания GIF. Загрузите видео, внесите некоторые изменения и сохраните его в формате GIF — приложение сделает вашу жизнь проще! Большинство форматов видео должны работать, так что дерзайте. А так как он сделан на Haskell, вы знаете, что это хорошо. Для командной строки есть графический интерфейс. Заядлый поклонник терминала? Приложение предлагает вам свой интерфейс командной строки. А для программистов на Haskell есть библиотека API. Приложение может сохранить ваше творение в виде GIF или видео. Так что, если вы страстно ненавидите GIF-файлы — нет проблем! Просто выберите «Сохранить как видео» и внесите свой вклад в избавление мира от GIF-файлов. Наслаждаетесь мемами? Отлично! Приложение может добавлять текст по всему вашему GIF. Вы можете изменить шрифт, размер, цвет, положение, контур, поворот и время. Создайте следующий вирусный мем!', null, 'FSFAP', 20, 3, '2021-02-15 19:01:05.000000', 336, 7973);
