INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (241, 'Subin', 'Редактор векторной графики', 'Бесплатный редактор векторной графики с открытым исходным кодом. Он предлагает богатый набор функций и широко используется как для художественных, так и для технических иллюстраций, таких как мультфильмы, картинки, логотипы, типографика, диаграммы и блок-схемы. Он использует векторную графику, чтобы обеспечить четкие распечатки и визуализацию с неограниченным разрешением и не привязан к фиксированному количеству пикселей, как растровая графика. Использует стандартизированный формат файла SVG в качестве основного формата, который поддерживается многими другими приложениями, включая веб-браузеры. Поддерживает множество расширенных функций SVG (маркеры, клоны, альфа-смешивание и т. д.), и большое внимание уделяется разработке оптимизированного интерфейса. Очень легко редактировать узлы, выполнять сложные операции с путями, отслеживать растровые изображения и многое другое. Мы также стремимся поддерживать процветающее сообщество пользователей и разработчиков, используя открытую разработку, ориентированную на сообщество.', null, 'TORQUE-1.1', 1, 3, '2021-02-13 07:42:36.000000', 4639, 9505);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (339, 'Alpha', 'Программа обработки изображений', 'Независимо от того, являетесь ли вы графическим дизайнером, фотографом, иллюстратором или ученым, программное обеспечение предоставляет вам сложные инструменты для выполнения вашей работы. Вы можете еще больше повысить свою производительность с помощью программы благодаря множеству параметров настройки и сторонних плагинов.', null, 'CC-BY-NC-3.0', 23, 3, '2022-05-20 13:18:47.000000', 4374, 9961);
