INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (274, 'Vagram', 'Цифровое творчество', 'В приложении есть все традиционные инструменты, которые вы любите из других пакетов для цифровой живописи и редактирования изображений, такие как инструмент «Волшебная палочка», мощные инструменты выделения и высококачественные кисти. Но данный софт добавляет к этой смеси свою изюминку. Ваши инструменты могут не только наносить цвет, но также дополнительно материалы и неровности. Это дает изображениям, которые вы создаете, 3D-атрибуты, которые визуализируются в реальном времени на вашем графическом процессоре с использованием света и теней, что приводит к потрясающим и уникальным изображениям. Возьмем, к примеру, прикрепленный скриншот «Каменного великана». Он был окрашен светоотражающими материалами, выпуклостями и освещен двумя источниками света. Вы можете свободно перемещать источники света, чтобы увидеть, как это меняет настроение, освещение и тени сцены. Материалы и источники света в приложении используют самые современные технологии, такие как PBR и GGX.', null, 'Unlicense', 26, 3, '2022-02-10 17:40:47.000000', 4665, 6703);
