INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (482, 'Cardguard', 'Средство параметрического 3D-моделирования', 'Программа для параметрического 3D-моделирования. Параметрическое моделирование позволяет легко модифицировать проект, возвращаясь к истории модели и изменяя ее параметры. Приложение имеет открытый исходный код и полностью модульный, что позволяет использовать расширенные возможности расширения и настройки. Приложение является мультиплатформенным и читает и записывает многие открытые форматы файлов, такие как STEP, IGES, STL и другие.', null, 'CC-BY-ND-3.0-DE', 19, 3, '2021-08-13 05:51:45.000000', 677, 5660);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (293, 'Tres-Zap', 'Приложение для дизайна интерьера', 'Это бесплатное приложение для дизайна интерьера, которое поможет вам разместить мебель на 2D-плане дома с предварительным 3D-просмотром. Доступная бесплатно, эта программа предназначена для людей, которые хотят быстро спроектировать свой интерьер, независимо от того, переезжают ли они или просто хотят изменить дизайн своего существующего дома. Многочисленные визуальные подсказки помогут вам нарисовать план вашего дома и расставить мебель. Вы можете нарисовать стены своих комнат на изображении существующего плана, а затем перетащить на план мебель из каталога, организованного по категориям. Каждое изменение в 2D-плане одновременно обновляется в 3D-виде, чтобы показать вам реалистичную визуализацию вашего макета.', null, 'CC-BY-NC-ND-4.0', 25, 3, '2022-02-18 19:30:33.000000', 1981, 9792);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (296, 'Bigtax', 'Видеоредактор для создания GIF', 'Это видеоредактор с открытым исходным кодом, построенный на Haskell, для создания GIF. Загрузите видео, внесите некоторые изменения и сохраните его в формате GIF — приложение сделает вашу жизнь проще! Большинство форматов видео должны работать, так что дерзайте. А так как он сделан на Haskell, вы знаете, что это хорошо. Для командной строки есть графический интерфейс. Заядлый поклонник терминала? Приложение предлагает вам свой интерфейс командной строки. А для программистов на Haskell есть библиотека API. Приложение может сохранить ваше творение в виде GIF или видео. Так что, если вы страстно ненавидите GIF-файлы — нет проблем! Просто выберите «Сохранить как видео» и внесите свой вклад в избавление мира от GIF-файлов. Наслаждаетесь мемами? Отлично! Приложение может добавлять текст по всему вашему GIF. Вы можете изменить шрифт, размер, цвет, положение, контур, поворот и время. Создайте следующий вирусный мем!', null, 'AMPAS', 20, 3, '2022-02-26 17:09:24.000000', 3315, 5587);
