INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (337, 'Andalax', '3D CAD', 'Это программное обеспечение для создания твердых 3D-объектов САПР. Он фокусируется на аспектах САПР, а не на художественных аспектах. Приложение не является интерактивным средством моделирования. Вместо этого это что-то вроде 3D-компилятора, который считывает файл сценария, описывающий объект, и рендерит 3D-модель из этого сценария. Это дает разработчику полный контроль над процессом моделирования и позволяет ему легко изменять любой шаг в процессе моделирования или создавать конструкции, определяемые настраиваемыми параметрами. ПРИМЕЧАНИЕ. Это моментальный снимок разработки, автоматически созданный из исходного репозитория.', null, 'YPL-1.1', 32, 3, '2021-03-26 20:12:51.000000', 3588, 5802);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (341, 'Alphazap', 'Редактор векторной графики', 'Бесплатный редактор векторной графики с открытым исходным кодом. Он предлагает богатый набор функций и широко используется как для художественных, так и для технических иллюстраций, таких как мультфильмы, картинки, логотипы, типографика, диаграммы и блок-схемы. Он использует векторную графику, чтобы обеспечить четкие распечатки и визуализацию с неограниченным разрешением и не привязан к фиксированному количеству пикселей, как растровая графика. Использует стандартизированный формат файла SVG в качестве основного формата, который поддерживается многими другими приложениями, включая веб-браузеры. Поддерживает множество расширенных функций SVG (маркеры, клоны, альфа-смешивание и т. д.), и большое внимание уделяется разработке оптимизированного интерфейса. Очень легко редактировать узлы, выполнять сложные операции с путями, отслеживать растровые изображения и многое другое. Мы также стремимся поддерживать процветающее сообщество пользователей и разработчиков, используя открытую разработку, ориентированную на сообщество.', null, 'SSPL-1.0', 1, 3, '2023-02-10 11:16:54.000000', 2116, 8138);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (559, 'Asoka', 'Программа обработки изображений', 'Независимо от того, являетесь ли вы графическим дизайнером, фотографом, иллюстратором или ученым, программное обеспечение предоставляет вам сложные инструменты для выполнения вашей работы. Вы можете еще больше повысить свою производительность с помощью программы благодаря множеству параметров настройки и сторонних плагинов.', null, 'LiLiQ-P-1.1', 46, 3, '2021-07-17 06:38:00.000000', 2617, 9699);
