INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (396, 'Sonsing', 'Видеоредактор для создания GIF', 'Это видеоредактор с открытым исходным кодом, построенный на Haskell, для создания GIF. Загрузите видео, внесите некоторые изменения и сохраните его в формате GIF — приложение сделает вашу жизнь проще! Большинство форматов видео должны работать, так что дерзайте. А так как он сделан на Haskell, вы знаете, что это хорошо. Для командной строки есть графический интерфейс. Заядлый поклонник терминала? Приложение предлагает вам свой интерфейс командной строки. А для программистов на Haskell есть библиотека API. Приложение может сохранить ваше творение в виде GIF или видео. Так что, если вы страстно ненавидите GIF-файлы — нет проблем! Просто выберите «Сохранить как видео» и внесите свой вклад в избавление мира от GIF-файлов. Наслаждаетесь мемами? Отлично! Приложение может добавлять текст по всему вашему GIF. Вы можете изменить шрифт, размер, цвет, положение, контур, поворот и время. Создайте следующий вирусный мем!', null, 'Abstyles', 23, 3, '2021-03-03 14:29:34.000000', 4632, 6933);
