INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (443, 'Vagram', 'Приложение для дизайна интерьера', 'Это бесплатное приложение для дизайна интерьера, которое поможет вам разместить мебель на 2D-плане дома с предварительным 3D-просмотром. Доступная бесплатно, эта программа предназначена для людей, которые хотят быстро спроектировать свой интерьер, независимо от того, переезжают ли они или просто хотят изменить дизайн своего существующего дома. Многочисленные визуальные подсказки помогут вам нарисовать план вашего дома и расставить мебель. Вы можете нарисовать стены своих комнат на изображении существующего плана, а затем перетащить на план мебель из каталога, организованного по категориям. Каждое изменение в 2D-плане одновременно обновляется в 3D-виде, чтобы показать вам реалистичную визуализацию вашего макета.', null, 'MIT-Modern-Variant', 6, 3, '2021-09-06 07:08:47.000000', 1579, 8727);
