INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (461, 'Sub-Ex', 'Редактор векторной графики', 'Бесплатный редактор векторной графики с открытым исходным кодом. Он предлагает богатый набор функций и широко используется как для художественных, так и для технических иллюстраций, таких как мультфильмы, картинки, логотипы, типографика, диаграммы и блок-схемы. Он использует векторную графику, чтобы обеспечить четкие распечатки и визуализацию с неограниченным разрешением и не привязан к фиксированному количеству пикселей, как растровая графика. Использует стандартизированный формат файла SVG в качестве основного формата, который поддерживается многими другими приложениями, включая веб-браузеры. Поддерживает множество расширенных функций SVG (маркеры, клоны, альфа-смешивание и т. д.), и большое внимание уделяется разработке оптимизированного интерфейса. Очень легко редактировать узлы, выполнять сложные операции с путями, отслеживать растровые изображения и многое другое. Мы также стремимся поддерживать процветающее сообщество пользователей и разработчиков, используя открытую разработку, ориентированную на сообщество.', null, 'CC-BY-NC-SA-3.0-DE', 29, 3, '2021-06-03 14:29:09.000000', 1816, 7291);
