INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (509, 'Holdlamis', 'Программа обработки изображений', 'Независимо от того, являетесь ли вы графическим дизайнером, фотографом, иллюстратором или ученым, программное обеспечение предоставляет вам сложные инструменты для выполнения вашей работы. Вы можете еще больше повысить свою производительность с помощью программы благодаря множеству параметров настройки и сторонних плагинов.', null, 'CC-BY-SA-2.0-UK', 33, 3, '2021-01-10 14:31:45.000000', 4272, 9978);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (563, 'Vagram', 'Приложение для дизайна интерьера', 'Это бесплатное приложение для дизайна интерьера, которое поможет вам разместить мебель на 2D-плане дома с предварительным 3D-просмотром. Доступная бесплатно, эта программа предназначена для людей, которые хотят быстро спроектировать свой интерьер, независимо от того, переезжают ли они или просто хотят изменить дизайн своего существующего дома. Многочисленные визуальные подсказки помогут вам нарисовать план вашего дома и расставить мебель. Вы можете нарисовать стены своих комнат на изображении существующего плана, а затем перетащить на план мебель из каталога, организованного по категориям. Каждое изменение в 2D-плане одновременно обновляется в 3D-виде, чтобы показать вам реалистичную визуализацию вашего макета.', null, 'MIT-Modern-Variant', 28, 3, '2022-08-06 04:36:58.000000', 1381, 5086);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (514, 'Tresom', 'Цифровое творчество', 'В приложении есть все традиционные инструменты, которые вы любите из других пакетов для цифровой живописи и редактирования изображений, такие как инструмент «Волшебная палочка», мощные инструменты выделения и высококачественные кисти. Но данный софт добавляет к этой смеси свою изюминку. Ваши инструменты могут не только наносить цвет, но также дополнительно материалы и неровности. Это дает изображениям, которые вы создаете, 3D-атрибуты, которые визуализируются в реальном времени на вашем графическом процессоре с использованием света и теней, что приводит к потрясающим и уникальным изображениям. Возьмем, к примеру, прикрепленный скриншот «Каменного великана». Он был окрашен светоотражающими материалами, выпуклостями и освещен двумя источниками света. Вы можете свободно перемещать источники света, чтобы увидеть, как это меняет настроение, освещение и тени сцены. Материалы и источники света в приложении используют самые современные технологии, такие как PBR и GGX.', null, 'LPL-1.0', 43, 3, '2021-04-30 08:29:25.000000', 3818, 7771);
