INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (647, 'Regrant', '3D CAD', 'Это программное обеспечение для создания твердых 3D-объектов САПР. Он фокусируется на аспектах САПР, а не на художественных аспектах. Приложение не является интерактивным средством моделирования. Вместо этого это что-то вроде 3D-компилятора, который считывает файл сценария, описывающий объект, и рендерит 3D-модель из этого сценария. Это дает разработчику полный контроль над процессом моделирования и позволяет ему легко изменять любой шаг в процессе моделирования или создавать конструкции, определяемые настраиваемыми параметрами. ПРИМЕЧАНИЕ. Это моментальный снимок разработки, автоматически созданный из исходного репозитория.', null, 'Dotseqn', 18, 3, '2021-02-18 07:39:51.000000', 954, 9358);
