INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (922, 'Gembucket', 'Средство параметрического 3D-моделирования', 'Программа для параметрического 3D-моделирования. Параметрическое моделирование позволяет легко модифицировать проект, возвращаясь к истории модели и изменяя ее параметры. Приложение имеет открытый исходный код и полностью модульный, что позволяет использовать расширенные возможности расширения и настройки. Приложение является мультиплатформенным и читает и записывает многие открытые форматы файлов, такие как STEP, IGES, STL и другие.', null, 'OFL-1.0', 38, 3, '2022-08-08 10:00:56.000000', 2649, 7811);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (847, 'Cookley', 'Бесплатный офисный пакет', 'Это бесплатный офисный пакет с открытым исходным кодом, разработанный The Mark Corp. Пакет включает программы для обработки текстов, создания и редактирования электронных таблиц, слайд-шоу, диаграмм и рисунков, работы с базами данных и составления математических формул.', null, 'WTFPL', 28, 5, '2021-11-13 00:37:07.000000', 2613, 5991);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (923, 'Kanlam', 'Приложение для дизайна интерьера', 'Это бесплатное приложение для дизайна интерьера, которое поможет вам разместить мебель на 2D-плане дома с предварительным 3D-просмотром. Доступная бесплатно, эта программа предназначена для людей, которые хотят быстро спроектировать свой интерьер, независимо от того, переезжают ли они или просто хотят изменить дизайн своего существующего дома. Многочисленные визуальные подсказки помогут вам нарисовать план вашего дома и расставить мебель. Вы можете нарисовать стены своих комнат на изображении существующего плана, а затем перетащить на план мебель из каталога, организованного по категориям. Каждое изменение в 2D-плане одновременно обновляется в 3D-виде, чтобы показать вам реалистичную визуализацию вашего макета.', null, 'GFDL-1.2-no-invariants-or-later', 10, 3, '2022-03-18 07:59:26.000000', 1357, 8493);
