INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (977, 'Treeflex', '3D CAD', 'Это программное обеспечение для создания твердых 3D-объектов САПР. Он фокусируется на аспектах САПР, а не на художественных аспектах. Приложение не является интерактивным средством моделирования. Вместо этого это что-то вроде 3D-компилятора, который считывает файл сценария, описывающий объект, и рендерит 3D-модель из этого сценария. Это дает разработчику полный контроль над процессом моделирования и позволяет ему легко изменять любой шаг в процессе моделирования или создавать конструкции, определяемые настраиваемыми параметрами. ПРИМЕЧАНИЕ. Это моментальный снимок разработки, автоматически созданный из исходного репозитория.', null, 'OSL-1.0', 10, 3, '2021-12-07 09:14:49.000000', 2571, 8702);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (954, 'Tampflex', 'Цифровое творчество', 'В приложении есть все традиционные инструменты, которые вы любите из других пакетов для цифровой живописи и редактирования изображений, такие как инструмент «Волшебная палочка», мощные инструменты выделения и высококачественные кисти. Но данный софт добавляет к этой смеси свою изюминку. Ваши инструменты могут не только наносить цвет, но также дополнительно материалы и неровности. Это дает изображениям, которые вы создаете, 3D-атрибуты, которые визуализируются в реальном времени на вашем графическом процессоре с использованием света и теней, что приводит к потрясающим и уникальным изображениям. Возьмем, к примеру, прикрепленный скриншот «Каменного великана». Он был окрашен светоотражающими материалами, выпуклостями и освещен двумя источниками света. Вы можете свободно перемещать источники света, чтобы увидеть, как это меняет настроение, освещение и тени сцены. Материалы и источники света в приложении используют самые современные технологии, такие как PBR и GGX.', null, 'PHP-3.01', 15, 3, '2022-05-01 16:37:45.000000', 3074, 5671);
