INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (928, 'Latlux', 'Лучшее почтовое приложение', 'Это новая версия почтового клиента, поддерживаемая одним из первоначальных авторов. Это быстрее, компактнее и с доставкой уже сегодня! Он заменяет код синхронизации JavaScript новым механизмом синхронизации C++, основанным на Mailcore2. Он использует намного меньше ОЗУ и ЦП и работает без «пробуждения ЦП», что означает большое время автономной работы. Он также имеет полностью обновленный композитор и другие замечательные новые функции. Приложение поставляется с мощными функциями, такими как Unified Inbox, Snooze, Send Later, Mail Rules, Templates и другими. Pro режим, который вы можете разблокировать с ежемесячной подпиской, добавляет еще больше функций для людей, которые отправляют тонны электронной почты: отслеживание ссылок, уведомления о прочтении, аналитика почтовых ящиков, контакты и профили компаний. Все эти функции работают в клиенте — приложение не отправляет ваши учетные данные электронной почты в облако.', null, 'CC-BY-NC-3.0-DE', 39, 5, '2023-02-14 17:32:26.000000', 4891, 5823);
