INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (151, 'Span', 'Порт, управляемый сообществом', 'Сегодня вы должны помнить много паролей. Вам нужен пароль для многих веб-сайтов, вашей учетной записи электронной почты, вашего веб-сервера, входа в сеть и т. Д. Список бесконечен. Кроме того, вы должны использовать разные пароли для каждой учетной записи, потому что, если вы будете использовать только один пароль везде и кто-то получит этот пароль, у вас будет проблема: вор будет иметь доступ ко всем вашим учетным записям. Этот бесплатный менеджер паролей с открытым исходным кодом поможет вам безопасно управлять своими паролями. Вы можете хранить все свои пароли в одной базе данных, которая заблокирована мастер-ключом. Таким образом, вам нужно запомнить только один главный ключ, чтобы разблокировать всю базу данных. Файлы базы данных шифруются с использованием лучших и наиболее безопасных алгоритмов шифрования, известных в настоящее время (AES-256, ChaCha20 и Twofish). Для получения дополнительной информации см. страницу характеристик.', null, 'SCEA', 43, 6, '2021-01-16 16:02:12.000000', 2362, 5292);
