INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (925, 'Lotstring', 'Управление командой', 'Социальная сеть микроблогинга, которая позволяет пользователям публиковать короткие текстовые записи длиной до 280 символов. Она была создана в 2003 году и стала популярной платформой для обмена новостями, идеями и мнениями. Основная функция платформы - это оставлять короткие записи, которые называются твитами. Они могут содержать фото, видео, ссылки и хэштеги. Пользователи могут подписываться на других пользователей и читать их публикации, а также отвечать их сообщениями. Приложение также позволяет пользователю настроить приватность своего аккаунта и ограничить доступ к своим записям.', null, 'PolyForm-Small-Business-1.0.0', 46, 4, '2021-07-22 13:55:59.000000', 1511, 8051);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (947, 'It', 'Конференции через Интернет', 'Онлайн-платформа для коммуникаций внутри команд и организаций. Она была создана в 2013 году. Приложение предоставляет пользователю возможность обмениваться сообщениями, файлами, документами и другой информацией в режиме реального времени. Основная функция платформы - это создание рабочих команд, в которых участники могут обсуждать проекты, делиться новостями и файлами, а также использовать интеграции со сторонними сервисами, такими как Dropbox и Google Drive.', null, 'AAL', 26, 4, '2021-01-06 18:17:29.000000', 4633, 7447);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (956, 'Konklab', 'Защищенный обмен сообщениями', 'Приложение стало незаменимым источником новостей и мнений для журналистов, политиков, бизнесменов и публичных личностей. Он предлагает рекламные возможности для бизнеса, позволяя продвигать бренд, товары и услуги в целевой аудитории. Приложение продолжает развиваться и увеличивать свою популярность в социальной сети. С момента создания он остается одной из самых важных платформ для обмена новостями и мнениями, а также для продвижения и развития бизнеса.', null, 'APSL-2.0', 40, 4, '2022-12-16 15:48:40.000000', 3108, 7385);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (958, 'Tresom', 'Новости..Политика..Отправить', 'Приложение предлагает широкий спектр функциональных возможностей, которые позволяют управлять рабочими командами, делиться материалами, настраивать уведомления, вызывать ботов и многое другое. Он также предлагает интеграцию с приложениями, которые используются в работе, такими как CRM-системы, проектные менеджеры и другие сервисы. Приложение стало популярным среди команд и организаций, которые работают удаленно. Он обеспечивает связь между участниками команды, улучшает работу и способствует более эффективному обмену информацией. Платформа также используется в рамках корпоративных коммуникаций и предоставляет инструменты для упрощения взаимодействия между отделами и сотрудниками.', null, 'RHeCos-1.1', 16, 4, '2022-03-11 03:24:16.000000', 4310, 7445);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (967, 'Trippledex', 'Конференции через Интернет', 'Онлайн-платформа для коммуникаций внутри команд и организаций. Она была создана в 2013 году. Приложение предоставляет пользователю возможность обмениваться сообщениями, файлами, документами и другой информацией в режиме реального времени. Основная функция платформы - это создание рабочих команд, в которых участники могут обсуждать проекты, делиться новостями и файлами, а также использовать интеграции со сторонними сервисами, такими как Dropbox и Google Drive.', null, 'SPL-1.0', 3, 4, '2022-10-02 20:44:23.000000', 1808, 8302);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (969, 'Y-find', 'Увидется со всем миром', 'Технология голосовой и видеосвязи через Интернет. Она была создана в 2003 году и позволяет пользователям звонить и общаться оффлайн через интернет. С помощью приложения пользователи могут совершать бесплатные голосовые и видеозвонки друг другу, обмениваться текстовыми сообщениями и файлами, а также использовать платформу для деловой связи. Приложение также предоставляет услуги платных звонков на телефоны или мобильные устройства, доступны по всему миру.', null, 'CECILL-1.1', 34, 4, '2021-06-04 01:21:31.000000', 2183, 8826);
INSERT INTO public.application (id, name, short_description, long_description, logo_path, license, user_id, category_id, last_update, downloads, views) VALUES (2, 'Bearly', 'Обмен сообщениями через интернет', 'Социальная сеть позволяет общаться и находить друзей, слушать музыку, смотреть фильмы, играть в игры и многое другое. Она также предлагает рекламные возможности для бизнеса - достигать целевой аудитории, продвигать бренд или продукт, создавать сообщества и многое другое. Приложение продолжает увеличивать свою популярность среди пользователей и становится важной частью культуры онлайн-общения во всем мире.', null, 'GPL-1.0-or-later', 40, 4, '2021-10-18 09:33:43.000000', 317, 5158);
